import * as React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { Identificador } from './Passo1/Identificador';
import { CadastroVeicular } from './Passo2/CadastroVeicular';
import { SolicitaServico } from './Passo3/SolicitaServico';

const steps = ['Identificação do Veículo','Cadastro do Veículo', 'Solicitação de Serviço', 'Revisão do Dados'];

export const Service = ({loginUser}) => {
    const [activeStep, setActiveStep] = React.useState(0);
    const [placa, setPlaca] = React.useState('');
    const [cad, setCad] = React.useState(false)

    var valida = true

    const handleNext = () => {
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
      setCad(false)
    };
  
    const handleBack = () => {
      setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };
  
    const handleReset = () => {
      setActiveStep(0);
    };

    if((activeStep === 0) && (placa.length === 7)){
      valida = false
    } else if ((activeStep === 1) && (cad === true)) {
      valida = false
    } else if ((activeStep === 2) && (placa.length === 7)) {
      valida = false
    } else if ((activeStep === 3) && (placa.length === 7)) {
      valida = false
    } else {
      valida = true;
    }

    // console.log({
    //   activeStep,
    //   valida
    // })

    return (
      <Box sx={{ width: '100%' }}>
        <Stepper activeStep={activeStep}>
          {steps.map((label) => {
            const stepProps = {};
            const labelProps = {};
            return (
              <Step key={label} {...stepProps}>
                <StepLabel {...labelProps}>{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        {activeStep === steps.length ? (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1 }}>
              Serviço solicitado com sucesso
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Box sx={{ flex: '1 1 auto' }} />
              <Button onClick={handleReset}>Resetar</Button>
            </Box>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1 }}>Etapa {activeStep + 1}</Typography>
            <Box sx={{display: 'flex', flexDirection: 'row', flexWrap: 'wrap', pr: 2 , justifyContent: 'space-around'}} >
                {activeStep === 0 ? <Identificador placa={placa} setPlaca={setPlaca} /> : null}
                {activeStep === 1 ? <CadastroVeicular loginUser={loginUser} placa={placa} setCad={setCad} /> : null}
                {activeStep === 2 ? <SolicitaServico setActiveStep={setActiveStep} /> : null}
            </Box>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Button
                color="inherit"
                disabled={activeStep === 0}
                onClick={handleBack}
                sx={{ mr: 1 }}
              >
                Voltar
              </Button>
              <Box sx={{ flex: '1 1 auto' }} />
  
              <Button id={steps[activeStep]} disabled={valida} color='success' onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finalizar' : 'Próximo'}
              </Button>
            </Box>
          </React.Fragment>
        )}
      </Box>
    );
}
