import React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { TextField } from '@mui/material';

const steps = ['Buscar Veículo', 'Conferindo Veículo', 'Inserindo GRT','Validando Digital'];

export const CadGrt = () => {
  const [activeStep, setActiveStep] = React.useState(0);
  const [dado, setDado] = React.useState('')


  const cadastro = (e) => {
    setDado(e.target.value)
  }

  console.log(dado, activeStep)

  const handleNext = () => {
    if ((activeStep === 0) && (dado === '')) {
      console.log('Placa vazia')
    } else {
      console.log('Placa preenchida')
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  const Passo1 = () => {
    return(
        <div className='placa--input'>
          <TextField id='placa' name='placa' value={dado} onChange={cadastro} label='Placa' size='small' required fullWidth/>
          <p>{dado}</p>
          <p>*Digite a placa e clique em próximos</p>
        </div>
    )
  }
  const Passo2 = () => {
    return(
        <div className='placa--input'>
          
        </div>
    )
  }
  const Passo3 = () => {
    return(
      <div>
        <div className='placa--input'>
          {/* <TextField onChange={cadastro} label='Placa' size='small' fullWidth/><p>tesssssssssssste</p> */}
        </div>
      </div>
    )
  }
  const Passo4 = () => {
    return(
      <div>
        <div className='placa--input'>
          {/* <TextField onChange={(e) => setDado(e.target.value)} label='Placa' size='small' fullWidth/><p>tesssssssssssste</p> */}
        </div>
      </div>
    )
  }

  // console.log(activeStep)

  return (
    <Box sx={{ width: '100%' }}>
      <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      {activeStep === steps.length ? (
        <React.Fragment>
          <Typography sx={{ mt: 2, mb: 1 }}>
            All steps completed - you&apos;re finished
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
            <Box sx={{ flex: '1 1 auto' }} />
            <Button onClick={handleReset}>Reset</Button>
          </Box>
        </React.Fragment>
      ) : (
        <React.Fragment>
          <Typography sx={{ mt: 2, mb: 1 }}>Passo {activeStep + 1}</Typography>
          
          {activeStep === 0 ? <Passo1 /> : null}
          {activeStep === 1 ? <Passo2 /> : null}
          {activeStep === 2 ? <Passo3 /> : null}
          {activeStep === 3 ? <Passo4 /> : null}

          <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
            <Button
              color="inherit"
              disabled={activeStep === 0}
              onClick={handleBack}
              sx={{ mr: 1 }}
            >
              Voltar
            </Button>
            <Box sx={{ flex: '1 1 auto' }} />

            <Button onClick={handleNext}>
              {activeStep === steps.length - 1 ? 'Finalizar' : 'Próximo'}
            </Button>
          </Box>
        </React.Fragment>
      )}
    </Box>
  );
}
