    return (
        <>
            <Typography variant='h6'>Selecione o serviço desejado</Typography>
            <div className={style.opcoes}>
                <Box sx={{ width: '100%' }}>
                    <Paper sx={{ p: 2 }}>
                        <Box sx={{ width: '100%' }}>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>Eu, </Typography>
                                <div className={style.nome}>
                                    <FormControl fullWidth  variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={user.nome}
                                            sx={{width: '70vw'}}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                {/* <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' />
                                <Typography variant='h6' /> */}
                                <Typography variant='h6' noWrap={true}>, Identidade</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>nº</Typography>
                                <div className={style.nome}>
                                    <Typography variant='h6'> {user.rg} </Typography>
                                </div>
                                <Typography variant='h6'>, CPF nº</Typography>
                                <div className={style.nome}>
                                    <Typography variant='h6'> {user.rg} </Typography>
                                </div>
                                <Typography variant='h6'>, Telefone</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>nº</Typography>
                                <div className={style.nome}>
                                    <Typography variant='h6'> {user.tel} </Typography>
                                </div>
                                <Typography variant='h6'>, requeiro ao DETRAN/RS a realização do seguinte serviço,</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>Relato ao(s) veículos(s) placa(s)</Typography>
                                <div className={style.nome}>
                                    <Typography variant='h6'> {veiculo.placa} </Typography>
                                </div>
                                <Typography variant='h6'>, chassi(s)</Typography>
                                <div className={style.nome}>
                                    <Typography variant='h6'> {veiculo.chassi} </Typography>
                                </div>
                                <Typography variant='h6'>:</Typography>
                            </div>
                        </Box> <br />
                        <form>
                            <Box sx={{ width: '100%' }}>
                                <div className={style.selecionaServico}>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={teste}
                                            onChange={SelecionaServico}
                                        >
                                            {[...Array(23)].map((iten, i) => (
                                                <FormControlLabel key={i + 1} index={i + 1} value={service[i].id} control={<Radio size="small" />} label={service[i].label} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={teste}
                                            onChange={SelecionaServico}
                                        >
                                            {service.map((iten, i) => i > 22 && (
                                                <>
                                                    <FormControlLabel key={i + 1} index={i + 1} value={service[i].id} control={<Radio size='small' />} label={service[i].label} />
                                                    {service[i].id === '32' ?
                                                        <RadioGroup
                                                            defaultValue=""
                                                            name="select-service"
                                                            value={motivoplaca}
                                                            onChange={MotivoPlaca}
                                                        >
                                                            <div className={style.opcional}>
                                                                <FormControlLabel value='Furto/Roubo' control={<Radio size="small" />} label='furto/roubo' disabled={teste === '32' ? false : true} />
                                                                <FormControlLabel value='Perda/Extravio' control={<Radio size="small" />} label='perda/extravio' disabled={teste === '32' ? false : true} />
                                                                <FormControlLabel value='Outros' control={<Radio size="small" />} label='outros' disabled={teste === '32' ? false : true} />
                                                            </div>
                                                        </RadioGroup>
                                                        :
                                                        null
                                                    }
                                                    {service[i].id === '33' ?
                                                        <RadioGroup
                                                            defaultValue=""
                                                            name="select-service"
                                                            value={motivolacre}
                                                            onChange={MotivoLacre}
                                                        >
                                                            <div className={style.opcional}>
                                                                <FormControlLabel value='Furto/Roubo' control={<Radio size="small" />} label='furto/roubo' disabled={teste === '33' ? false : true} />
                                                                <FormControlLabel value='Perda/Extravio' control={<Radio size="small" />} label='perda/extravio' disabled={teste === '33' ? false : true} />
                                                                <FormControlLabel value='Outros' control={<Radio size="small" />} label='outros' disabled={teste === '33' ? false : true} />
                                                            </div>
                                                        </RadioGroup>
                                                        :
                                                        null
                                                    }
                                                </>
                                            ))}
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className={style.outro}>
                                    <RadioGroup
                                        defaultValue=""
                                        name="select-service"
                                        value={teste}
                                        onChange={SelecionaServico}
                                    >
                                        <FormControlLabel value={outros.id} control={<Radio size="small" />} label={outros.label} />
                                    </RadioGroup>
                                    <TextField variant="standard" size="small" disabled={valida} fullWidth />
                                </div>
                            </Box>
                            <Box sx={{ width: '100%' }}>
                                <div className={style.outro}>
                                    <Typography variant='h6'>Haverá inclusão ou alteração de restrição financeira? </Typography>
                                    <RadioGroup
                                        sx={{ ml: 2 }}
                                        defaultValue=""
                                        name="select-service"
                                        value={finan}
                                        onChange={FinanSelect}
                                    >
                                        <div className={style.outro}>
                                            <FormControlLabel value='sim' control={<Radio size="small" />} label={'Sim'} />
                                            <FormControlLabel value='nao' control={<Radio size="small" />} label={'Não'} />
                                        </div>
                                    </RadioGroup>
                                </div>
                                <div className={style.finan}>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={finan1.id}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan1.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                        <div className={style.outro}>
                                            <Typography >O credor é: </Typography>
                                            <TextField variant="standard" size="small" disabled={look}></TextField>
                                        </div>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={finan2.id}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan2.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={finan3.id}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan3.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className={style.finan}>
                                    <Typography>Agente Financeiro: </Typography>
                                    <TextField variant="standard" size="small" disabled={look} fullWidth></TextField>
                                </div>
                                <FormControlLabel
                                    defaultValue=""
                                    onChange={SelecionaEndereco}
                                    value='sim'
                                    label='Utilizar o endereço da CNH emitida no RS'
                                    control={<Checkbox size="small" />}
                                />
                                <Typography>Houve alteração de endereço após o último licenciamento do veículo?</Typography>
                                <RadioGroup
                                    defaultValue=""
                                    name="select-service"
                                    value={teste}
                                    onChange={SelecionaServico}
                                >
                                    <FormControlLabel value='nao' control={<Radio size="small" />} label='Não' />
                                    <FormControlLabel value={outros.id} control={<Radio size="small" />} label='Sim. Novo endereço, conforme comprovante de residência apresentado neste serviço:' />
                                </RadioGroup>
                                <TextField variant="standard" size="small" disabled={look} fullWidth></TextField>
                                <div className={style.container1}>
                                    <div className={style.outro}>
                                        <Typography>Telefone celular do Proprietário, adquirente, curador ou inventariante:</Typography>
                                        <TextField sx={{ width: '50%' }} variant="standard" size="small"></TextField>
                                    </div>
                                    <div className={style.outro}>
                                        <Typography>Email do Proprietário, adquirente, curador ou inventariante:</Typography>
                                        <TextField sx={{ width: '57%' }} variant="standard" size="small"></TextField>
                                    </div>
                                    <FormControlLabel
                                        defaultValue=""
                                        onChange={SelecionaEndereco}
                                        value='sim'
                                        label='Autorizo o DETRAN/RS a enviar por email ou telefone celular informações de meu interesse junto a este órgão.'
                                        control={<Checkbox size="small" />}
                                    />
                                </div>
                                <div className={style.container2}>
                                    <Typography align="center" variant="h6">DECLARO, sob as penas da lei e sem prejuízo de demais sanções administrativas/cíveis/criminais, que</Typography>
                                    <Typography align="center" variant="h6">os documentos entregues ao DETRAN/RS para o serviço requerido são todos autênticos e, quando cópias,</Typography>
                                    <Typography align="center" variant="h6">condizem com o original e, caso haja produção de placa(s) de identificação veicular, que sou responsável</Typography>
                                    <Typography align="center" variant="h6">por assegurar a correta fixação da(s) placa(s) no veículo para o qual a estampagem foi autorizada, bem </Typography>
                                    <Typography align="center" variant="h6">como pela entrega das placas retiradas/substituídas na EPIV</Typography>
                                </div>
                                <div className={style.containerAssina}>
                                    <div className={style.assinatura1}>
                                        <Typography>Município:</Typography>
                                        <Input
                                            id="standard-adornment-amount"
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography>, </Typography>
                                        <Input
                                            id="standard-adornment-amount"
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography> de </Typography>
                                        <Input
                                            id="standard-adornment-amount"
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography> de 20 </Typography>
                                        <Input
                                            id="standard-adornment-amount"
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography>.</Typography>
                                    </div>
                                    <div className={style.assinatura2}>
                                        <div className={style.linha}></div>
                                        <Typography>Assinatura do Responsável</Typography>
                                    </div>
                                </div>
                            </Box>
                        </form>
                    </Paper>
                </Box>
            </div>
        </>
    )
}
