import React from "react";
import { List, ListItemButton, ListItemText, Typography } from "@mui/material"
import style from './SolicitaServico.module.scss';
import { Box } from "@mui/system";

export const SolicitaServico = ({valida}) => {

    const [selectedIndex, setSelectedIndex] = React.useState(0);

    const servicos = [
        {
            title: "Impressão do CRLVe",
            id: 0,
        },
        {
            title: "Emissão de Certidão",
            id: 1,
        },
        {
            title: "Baixa simples do Veículo",
            id: 2,
        },
        {
            title: "Solicitação de Vistoria",
            id: 3,
        },
        {
            title: "Transferência de propriedade de Veìculo de Outra UF",
            id: 4,
        },
        {
            title: "Primeiro Emplacamento",
            id: 5,
        },
        {
            title: "Comunicação de Venda",
            id: 6,
        }

    ]

    const handleListItemClick = (event, index) => {
        setSelectedIndex(index);
        valida === false
    };

    return (
        <>
            <Typography variant='h6'>Selecione o serviço desejado</Typography>
            <div className={style.opcoes}>
                <Box sx={{ width: '100%', bgcolor: 'background.paper' }}>
                    <nav aria-label="secondary mailbox folders">
                        <List component="nav" aria-label="secondary mailbox folder" sx={{display: 'flex', flexWrap: 'wrap'}} dense="dense">
                            {servicos.map((itens) => (
                            <ListItemButton
                                sx={{minWidth: '25%'}}
                                selected={selectedIndex === itens.id}
                                onClick={(e) => handleListItemClick(e, itens.id)}
                            >
                                <ListItemText primary={itens.title} align='center' />
                            </ListItemButton>
                            ))}
                        </List>
                    </nav>
                </Box>
            </div>
        </>
    )
}
