import React from 'react';
import '../../sass/Tela1/tela1.css'
import IAExec from './IncluAverbExec';
import IPEFab from './IncluPlacaExFab';
import IRFinan from './IncluRestFinan';

const Tela6 = () => {

    const [tela1, setTela1] = React.useState(false);
    const [tela2, setTela2] = React.useState(false);
    const [tela3, setTela3] = React.useState(false);

    const botaoOpt1 = () => {
        tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
    }
    const botaoOpt2 = () => {
        tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
    }
    const botaoOpt3 = () => {
        tela1 ? setTela1(tela1) : setTela1(!tela1);
        !tela2 ? setTela2(tela2) : setTela2(!tela2);
        !tela3 ? setTela3(tela3) : setTela3(!tela3);
    }

    return (
        <>
            <div className='tela--inicial--tela1'>
                <p className='item' onClick={botaoOpt1} >Inclusão de Restrição Financeira</p>
                <p className='item' onClick={botaoOpt2} >Inclusão de Placa de Experiências ou de Fabricante</p>
                <p className='item' onClick={botaoOpt3} >Inclusão de Averbação de Execução</p>
            </div>
            {!tela1?null:<IAExec />}
            {!tela2?null:<IPEFab />}
            {!tela3?null:<IRFinan />}
        </>
    )
}

export default Tela6
