import React from 'react';
import { useEffect } from 'react';
import { ApesquisaRealizada, Modal } from '../../api/alerta/alertas';

const AIProp = () => {

    const [dado, setDado] = React.useState();
    const [opt, setOpt] = React.useState();

    const EnvioDados = (e) => {
        e.preventDefault()
        console.log('enviado', dado, opt)
        Modal()
    }

    useEffect(() => {
        if(opt === 'placa'){
            document.getElementById('btn').placeholder='Digite a placa aqui!'
        } else if(opt === 'chassi'){
            document.getElementById('btn').placeholder='Digite o chassi aqui!'
        } 
    },[opt])
    useEffect(() => {
        if(typeof(opt) === 'undefined'){
            alert('Escolha um tipo de documento!')
        }
    },[dado,opt])

    console.log(dado, 'teste', opt)

    return (
        <div className='tela--geral'>
            <div className='tela--consulta'>
                <h1>Alteração de Informações do Proprietário</h1>
                <form onSubmit={EnvioDados}>
                    <fieldset>
                        <label id='opt' htmlFor='placa'>PLACA: </label>
                        <input name='doc'className='radio' id='placa' onClick={(e) => setOpt(e.target.id)} value='placa' type='radio'></input>
                        <label id='opt' htmlFor='placa'>CHASSI:</label>
                        <input name='doc' className='radio' id='chassi' onClick={(e) => setOpt(e.target.id)} value='chassi' type='radio'></input>
                    </fieldset>
                    <div>
                        <h4>*Não inserir ponto(.) virgula(,) traço(-)</h4>
                        <h4>*Apenas numeros e letrar</h4>
                    </div>
                    <label> </label>
                    <input id='btn' onChange={(e) => setDado(e.target.value)} placeholder='Selecione a entrada!' type='text' />
                    <button type='onsubmit'>enviar</button>
                </form>
            </div>
            <ApesquisaRealizada />
        </div>
    )
}
export default AIProp
