import React from 'react';
import { useState } from 'react';
import { Cabecalho } from '../../api/cabecalho/Cabecalho'
import style from './Dashboard.module.scss'
import { TelaInicial } from './Inical/Inicial';
import { List, ListSubheader, ListItemButton, ListItemIcon, ListItemText, Collapse } from "@mui/material";
import HomeIcon from '@mui/icons-material/Home';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import GroupIcon from '@mui/icons-material/Group';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import { CadastroUsuario } from './Usuario/Cadastro/CadastroUsuario';
import { ListarUsuario } from './Usuario/Listar/ListarUsuario';
import { EditarUsuario } from './Usuario/Editar/EditarUsuario';
import * as Scroll from 'react-scroll';
import { Link, Button, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'


const Dashboard = () => {

    const outdoor = [
        "Tela Inicial",
        "Cadastro de Usu치rio",
        "Lista de Usu치rio",
        "Detalhes do Usu치rio",
        "Editar Usu치rio"
    ]

    const [tela, setTela] = useState({
        letreiro: outdoor[0],
        inicial: true,
        useradd: false,
        userlist: false,
        userEdit: false,
    })
    const [id, setId] = useState('')
    const [open, setOpen] = useState({
        bcadastro: false
    })


    const openUser = () => { setOpen(a => ({ ...a, bcadastro: !open.bcadastro })) }
    const telaInicial = () => { setTela(a => ({ ...a, inicial: true, useradd: false, userlist: false, userView: false, userEdit: false, letreiro: outdoor[0] })) }
    const addUser = () => { setTela(a => ({ ...a, inicial: false, useradd: true, userlist: false, userView: false, userEdit: false, letreiro: outdoor[1] })) }
    const listUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: true, userView: false, userEdit: false, letreiro: outdoor[2] })) }
    const editUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: true, letreiro: outdoor[4] })) }

    window.scrollTo({
        top: "250px",
        behavior: "smooth",
    })

    return (
        <>
            <Cabecalho />
            <Link activeClass="active"
                to="inicial"
                spy={true}
                smooth={true}
                hashSpy={true}
                offset={50}
                duration={500}
                delay={1000}
                isDynamic={true}
                onSetActive={this.handleSetActive}
                onSetInactive={this.handleSetInactive}
                ignoreCancelEvents={false}
                spyThrottle={500}
            >
                Your name
            </Link>
            <div className={style.containerFundo} id='inicial'>
                <div className={style.info}>
                    <p>teste</p>
                    <p>{tela.letreiro}</p>
                    <p>teste</p>
                </div>
                <div className={style.container}>
                    <aside className={style.lateral}>
                        <List
                            sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
                            dense={true}
                            component="nav"
                            aria-labelledby="nested-list-subheader"
                            subheader={
                                <ListSubheader component="div" id="nested-list-subheader">
                                    <p>Menu</p>
                                </ListSubheader>
                            }
                        >
                            <ListItemButton onClick={telaInicial}>
                                <ListItemIcon>
                                    <HomeIcon />
                                </ListItemIcon>
                                <ListItemText primary="Tela inicial" />
                            </ListItemButton>
                            <ListItemButton onClick={openUser}>
                                <ListItemIcon>
                                    <AccountCircleIcon />
                                </ListItemIcon>
                                <ListItemText primary="Usu치rio" />
                                {open.bcadastro ? <ExpandLess /> : <ExpandMore />}
                            </ListItemButton>
                            <Collapse in={open.bcadastro} timeout='auto' unmountOnExit>
                                <List dense={true} component='div' >
                                    <ListItemButton sx={{ pl: 4 }} onClick={addUser}>
                                        <ListItemIcon>
                                            <PersonAddIcon />
                                        </ListItemIcon>
                                        <ListItemText primary='Cadastrar usu치rio' />
                                    </ListItemButton>
                                    <ListItemButton sx={{ pl: 4 }} onClick={listUser}>
                                        <ListItemIcon>
                                            <GroupIcon />
                                        </ListItemIcon>
                                        <ListItemText primary='Listar usu치rio' />
                                    </ListItemButton>
                                </List>
                            </Collapse>
                        </List>
                    </aside>
                    <div className={style.boxCentral}>
                        <div className={style.boxCentralContainer}>
                            {tela.inicial ? <TelaInicial /> : null}
                            {tela.useradd ? <CadastroUsuario /> : null}
                            {tela.userlist ? <ListarUsuario setId={setId} editUser={editUser} /> : null}
                            {tela.userEdit ? <EditarUsuario id={id} listUser={listUser} /> : null}
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Dashboard;
