import * as React from 'react';
import { Alert, Box, Button, InputLabel, MenuItem, Paper, Select, Snackbar } from "@mui/material"
import axios from "axios"
import style from './UploadArquivo.module.scss'

export const UploadArquivo = ({ 
    user, 
    // veiculo,
    setColetaDocumento
}) => {

    const [imagens1, setImagens1] = React.useState([])
    const [imagens2, setImagens2] = React.useState([])
    const [imagens3, setImagens3] = React.useState([])
    const [imagensURLs1, setImagensURLs1] = React.useState([])
    const [imagensURLs2, setImagensURLs2] = React.useState([])
    const [imagensURLs3, setImagensURLs3] = React.useState([])

    React.useEffect(() => {
        const novaImagensURLs1 = [];
        const novaImagensURLs2 = [];
        const novaImagensURLs3 = [];
        if (imagens1.length < 1) return;
        imagens1.forEach(imagem => novaImagensURLs1.push(URL.createObjectURL(imagem)));
        setImagensURLs1(novaImagensURLs1);
        if (imagens2.length < 1) return;
        imagens2.forEach(imagem => novaImagensURLs2.push(URL.createObjectURL(imagem)));
        setImagensURLs2(novaImagensURLs2);
        if (imagens3.length < 1) return;
        imagens3.forEach(imagem => novaImagensURLs3.push(URL.createObjectURL(imagem)));
        setImagensURLs3(novaImagensURLs3);
    }, [imagens1, imagens2, imagens3])

    const onImagens = (e) => {
        // console.log(e.target.files)
        // console.log(e.target.name)
        e.target.name === 'rg' && setImagens1([...e.currentTarget.files])
        e.target.name === 'comprovante_endereco' && setImagens2([...e.currentTarget.files])
        e.target.name === 'documento_veiculo' && setImagens3([...e.currentTarget.files])
    }

    const [retorno, setRetorno] = React.useState()
    const [grt, setGrt] = React.useState("")

    const escolheGrt = (e) => {
        setGrt(e.target.value)
    }

    React.useEffect(() => {
        axios.get("http://localhost:3001/GRT/Utilizaveis", {
            params: { user }
        })
            .then(res => {
                // console.log(res.data)
                setRetorno(res.data)
            })
            .catch(err => {
                // console.log(err)
            })
    }, [])


    const salvarGrt = (e) => {
        e.preventDefault()
        // axios.post('http://localhost:3001/Servico/GravaGrt', {user, veiculo, grt, })
        // .then(res => {
        //     console.log(res)
        // })
        // .catch(err => console.log(err))
        try {
            avisoPositivo()
            setColetaDocumento(true)
        }
        catch {
            avisoNegativo()
        }
    }

    // console.log(user, retorno, grt, veiculo)

    const [ op, setOp ] = React.useState({
        op: false,
        tipo: 'info',
        frase: ''
    })
    const fecharAviso = () => { setOp(a => ({...a, op: false})) }
    const avisoPositivo = () => {
        setOp(a => ({...a,
            op: true,
            tipo: 'success',
            frase: 'Arquivos enviados com sucesso!'
        }))
    }
    const avisoNegativo = () => {
        setOp(a => ({...a,
            op: true,
            tipo: true,
            frase: 'Arquivo não enviados, tente novamente!'
        }))
    }
    
    var valida = true

    if (imagens1.length === 0 || imagens2.length === 0 || imagens3.length === 0 || grt !== null) {
        valida = true
    } else {
        valida = false
    }

    return (
        <>
        <Snackbar
            open={op.op}
            autoHideDuration={6000}
            onClose={fecharAviso}
        >
            <Alert onClose={fecharAviso} severity={op.tipo} sx={{ width: '100%'}} >
                {op.frase}
            </Alert>
        </Snackbar>
            <Paper sx={{ p: 2 }}>
                <Box sx={{ width: '100%' }}>
                    <form className={style.container} onSubmit={salvarGrt}>
                        <div className={style.botaoUp}>
                            <InputLabel id="selecionaGrt">Selecione a GRT</InputLabel>
                            <Select
                                labelId="selecionaGrt"
                                id="SelGRT"
                                value={grt}
                                label='Selecione a GRT'
                                onChange={escolheGrt}
                            >
                                {/* <MenuItem value={10}>dez</MenuItem>
                                <MenuItem value={20}>vinte</MenuItem> */}
                                {retorno !== undefined && retorno.map((iten) => {
                                    return (
                                        <MenuItem key={iten.id} value={iten.GRT}>{iten.GRT}</MenuItem>
                                    )
                                })}
                            </Select>
                        </div>
                        <div className={style.boxConteiner}>
                            <Button id='rg' sx={{ m: 2 }} variant="contained" component="label">
                                Enviar rg
                                <input hidden accept="image/*" type="file" name='rg' onChange={onImagens} />
                            </Button>
                            <div className={style.amostra}>
                                {imagens1.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs1.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)}
                            </div>
                        </div>
                        <div className={style.boxConteiner}>
                            <Button id='rg' sx={{ m: 2 }} variant="contained" component="label">
                                Enviar Comprovante de residencia
                                <input hidden accept="image/*" type="file" name='comprovante_endereco' onChange={onImagens} />
                            </Button>
                            <div className={style.amostra}>
                                {imagens2.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs2.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)}
                            </div>
                        </div>
                        <div className={style.boxConteiner}>
                            <Button id='rg' sx={{ m: 2 }} variant="contained" component="label">
                                Enviar Documento do Veículo
                                <input hidden accept="image/*" type="file" name='documento_veiculo' onChange={onImagens} />
                            </Button>
                            <div className={style.amostra}>
                                {imagens3.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs3.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)}
                            </div>
                        </div>

                        <div className={style.botao}>
                            <Button disabled={valida} color='success' type="submit">Salvar</Button>
                        </div>

                    </form>
                </Box>
            </Paper>
        </>
    )
}
