import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, Paper, Typography } from "@mui/material"
import { Box } from "@mui/system"
import AddIcon from '@mui/icons-material/Add';
import HorizontalRuleIcon from '@mui/icons-material/HorizontalRule';
import { useState } from "react";
import axios from "axios";

export const SolicitarGrt = ({ loginUser }) => {


    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const [ quantidade, setQuantidadse ] = useState(50)

    const [ valor, setValor ] = useState(1.5)

    const valorPadrão = 1.50
    var valida = false

    // const Add = () => {
    //     setQtd(a => ({ ...a, valor: qtd.valor + 1.50, quantidade: qtd.quantidade + 1 }))
    // }

    // const Sub = () => {
    //     setQtd(a => ({ ...a, valor: qtd.valor - 1.50, quantidade: qtd.quantidade - 1 }))
    // }

    // qtd.valor === 1.50 ? valida = true : valida = false

    
    const Solicitar = () => {

        // const d = new Date();
        
        // qtd['data'] = Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)
        // qtd['hora'] = Intl.DateTimeFormat('pt-BR', { hour: 'numeric', minute: 'numeric', second: 'numeric' }).format(d)

        // console.log(qtd)
        
        // axios.post('http://localhost:3001/grt/solicitar', {loginUser, qtd})
        // .then(res => {
        //     console.log(res)
        //     handleClose()
        // })
        // .catch(err => {
        //     console.log(err)
        // })
    }

    return (
        <>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    Confirmar a solicitação das GRTs
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Ao confirmar a solicitação será cobrado o valor de R$: {valor.toFixed(2)} pela
                        quantidade de {quantidade} GRTs solicitadas.
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={Solicitar} color='success' autoFocus>Confirmar</Button>
                    <Button onClick={handleClose} color='error'>
                        Cancelar
                    </Button>
                </DialogActions>
            </Dialog>


            <Typography variant='h6' color='primary'> Solicitar GRT</Typography>
            <Box sx={{ width: '100%' }}>
                <Paper sx={{ p: 4, m: 2 }} elevation={6} >
                    <Typography variant="h5">Valor por GRT: R$ {valorPadrão.toFixed(2)} </Typography>
                    <br /><br />
                    <Typography sx={{ p: 2 }} >Quantidade de GRTs:  {quantidade} </Typography>
                    <Typography sx={{ p: 2 }} >Valor total da GRT: R$ {valor.toFixed(2)} </Typography>

                    {/* <Box>
                        <IconButton onClick={Add}>
                            <AddIcon />
                        </IconButton>
                        <IconButton id='minus' onClick={Sub} disabled={valida} >
                            <HorizontalRuleIcon />
                        </IconButton>
                    </Box> */}
                    <br /><br />
                    <Button sx={{ left: 1 }} onClick={handleClickOpen} >Solicitar</Button>
                </Paper>
            </Box>
        </>
    )
}
