import { TextField } from "@mui/material"
import React from "react"
import style from './Identificador.module.scss'

export const Identificador = ({ placa, setPlaca }) => {

    var passo1 = false
    
    if (placa.length > 7) {
        passo1 = true
    }

    const Placa = (e) => {
        setPlaca(e.target.value.toUpperCase().replace(/[A-Z]{3}/, '').slice(0,7))
    }
