    //     // console.log(placa[4])
    //     // console.log(placa[5])
    //     // console.log(placa[6])

    // }, [placa])

    return (
        <>
            <div className={style.formulario}>
                <TextField
                    id="placa"
                    sx={{ minWidth: '30vw', margin: 2 }}
                    label='Placa'
                    placeholder='Digite a placa do veículo'
                    error={passo1}
                    helperText={passo1 ? "Placa inválida" : null}
                    value={placa}
                    onChange={Placa}
                    size='small'
                    required
                />
            </div>
        </>
    )
}
