import * as React from 'react';
import axios from 'axios';
import style from './Inicial.module.scss'

export const TelaInicial = ({user}) => {

    const [resposta, setResposta] = React.useState()

    React.useEffect(() => {
        console.log("teste")
        axios.get("http://localhost:3001/GRT/Disponiveis", {
            params: {user}
        })
        .then(res => {
            console.log("Foi aqui:", res)
            setResposta(res)
        })
        .catch(err => {
            console.log(err)
        })
    },[])

    const d = new Date();
    const dt = Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)   
    
    return(
        <div className={style["tela--inicial"]}>
            <p>CRDD-RS</p>
            <p>{dt}</p>
            <p>Seja bem vindo, {user}.</p>
            <h2>GRTs dispon√≠veis:</h2>
            {resposta !== undefined  ? resposta.data.length !== 0 ? resposta.map((iten) => {
                return(
                    <>
                        <h5 key={iten.id}>{iten}</h5>
                    </>
                )
            }): <h5>0</h5> : <h5>0</h5>}
        </div>
    )
}
