import { Button, ButtonGroup, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, Paper, Typography } from "@mui/material"
import { Box } from "@mui/system"
import AddIcon from '@mui/icons-material/Add';
import HorizontalRuleIcon from '@mui/icons-material/HorizontalRule';
import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import style from './CriarGrt.module.scss';

export const SolicitarGrt = ({ loginUser }) => {


    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const [quantidade, setQuantidade] = useState(0)

    const [valor, setValor] = useState(quantidade * 1.5)

    useEffect(() => {
        setValor(quantidade * 1.5)
    }, [quantidade])

    const valorPadrão = 1.50

    // const Add = () => {
    //     setQtd(a => ({ ...a, valor: qtd.valor + 1.50, quantidade: qtd.quantidade + 1 }))
    // }

    // const Sub = () => {
    //     setQtd(a => ({ ...a, valor: qtd.valor - 1.50, quantidade: qtd.quantidade - 1 }))
    // }

    // qtd.valor === 1.50 ? valida = true : valida = false


    const Solicitar = () => {

        const d = new Date();
        
        let GRT = {
            quantidade,
            valor,
            hora: Intl.DateTimeFormat('pt-BR', { hour: 'numeric', minute: 'numeric', second: 'numeric' }).format(d),
            data: Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)
        }
        console.log(GRT)

        // axios.post('http://localhost:3001/grt/solicitar', {loginUser, GRT})
        // .then(res => {
        //     console.log(res)
        //     handleClose()
        // })
        // .catch(err => {
        //     console.log(err)
        // })
    }

    return (
        <>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    Confirmar a solicitação das GRTs
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Ao confirmar a solicitação será cobrado o valor de R$: {valor.toFixed(2)} pela
                        quantidade de {quantidade} GRTs solicitadas.
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={Solicitar} color='success' autoFocus>Confirmar</Button>
                    <Button onClick={handleClose} color='error'>
                        Cancelar
                    </Button>
                </DialogActions>
            </Dialog>


            <Typography variant='h6' color='primary'> Solicitar GRT</Typography>
            <Box sx={{ width: '100%' }}>
                <Paper sx={{ p: 4, m: 2 }} elevation={6} >
                    <Typography variant="h5">Valor por GRT: R$ {valorPadrão.toFixed(2)} </Typography>
                    <br /><br />
                    <Typography sx={{ p: 2 }} variant='h6'>Quantidade de GRTs:  {quantidade} </Typography>
                    <Typography sx={{ p: 2 }} variant='h6'>Valor total da GRT: R$ {valor.toFixed(2)} </Typography>

                    {/* <div className={style.listabotao}>
                        <ButtonGroup variant="contained" aria-label='qtd_grt' >
                            <Button onClick={() => setQuantidade(50)}>50</Button>
                            <Button onClick={() => setQuantidade(100)}>100</Button>
                            <Button onClick={() => setQuantidade(150)}>150</Button>
                            <Button onClick={() => setQuantidade(200)}>200</Button>
                            <Button onClick={() => setQuantidade(250)}>250</Button>
                            <Button onClick={() => setQuantidade(300)}>300</Button>
                            <Button onClick={() => setQuantidade(350)}>350</Button>
                            <Button onClick={() => setQuantidade(400)}>400</Button>
                            <Button onClick={() => setQuantidade(450)}>450</Button>
                            <Button onClick={() => setQuantidade(500)}>500</Button>
                            <Button onClick={() => setQuantidade(550)}>550</Button>
                        </ButtonGroup>
                    </div> */}
                    <div className={style.listabotaocalc}>
                        {/* <ButtonGroup variant="contained" aria-label='qtd_grt_25' >
                            <Button disabled={quantidade < 50} onClick={() => setQuantidade(quantidade + 25)}>+ 25</Button>
                            <Button disabled={quantidade <= 50} onClick={() => setQuantidade(quantidade - 25)}>-25</Button>
                        </ButtonGroup> */}
                        <ButtonGroup variant="contained" aria-label='qtd_grt_50' >
                            <Button onClick={() => setQuantidade(quantidade + 50)}>+ 50</Button>
                            <Button disabled={quantidade <= 75} onClick={() => setQuantidade(quantidade - 50)}>-50</Button>
                        </ButtonGroup>
                        {/* <ButtonGroup variant="contained" aria-label='qtd_grt_100' >
                            <Button onClick={() => setQuantidade(quantidade + 100)}>+ 100</Button>
                            <Button disabled={quantidade <= 125} onClick={() => setQuantidade(quantidade - 100)}>-100</Button>
                        </ButtonGroup> */}
                    </div>
                    <br /><br />
                    <Button sx={{ left: 1 }} onClick={handleClickOpen} >Solicitar</Button>
                </Paper>
            </Box>
        </>
    )
}
