import React from 'react';
import '../../sass/Tela1/tela1.css'

const Tela7 = () => {

    const [tela1, setTela1] = React.useState(false);
    const [tela2, setTela2] = React.useState(false);
    const [tela3, setTela3] = React.useState(false);
    const [tela4, setTela4] = React.useState(false);

    const botaoOpt1 = () => {
        tela1 ? setTela1(tela1):setTela1(!tela1);
        !tela2 ? setTela2(tela2):setTela2(!tela2);
        !tela3 ? setTela3(tela3):setTela3(!tela3);
        !tela4 ? setTela4(tela4):setTela4(!tela4);
    }
    const botaoOpt2 = () => {
        !tela1 ? setTela1(tela1):setTela1(!tela1);
        tela2 ? setTela2(tela2):setTela2(!tela2);
        !tela3 ? setTela3(tela3):setTela3(!tela3);
        !tela4 ? setTela4(tela4):setTela4(!tela4);
    }
    const botaoOpt3 = () => {
        !tela1 ? setTela1(tela1):setTela1(!tela1);
        !tela2 ? setTela2(tela2):setTela2(!tela2);
        tela3 ? setTela3(tela3):setTela3(!tela3);
        !tela4 ? setTela4(tela4):setTela4(!tela4);
    }
    const botaoOpt4 = () => {
        !tela1 ? setTela1(tela1):setTela1(!tela1);
        !tela2 ? setTela2(tela2):setTela2(!tela2);
        !tela3 ? setTela3(tela3):setTela3(!tela3);
        tela4 ? setTela4(tela4):setTela4(!tela4);
    }
    
    return (
        <>
            <div className='tela--inicial--tela1'>
                <p className='item' onClick={botaoOpt1} >Transferência de Propriedade de Veículo de Outra UF</p>
                <p className='item' onClick={botaoOpt2} >Transferência de Propriedade de Veículo do RS</p>
                <p className='item' onClick={botaoOpt3} >Troca de Município de Veículo de Outra UF</p>
                <p className='item' onClick={botaoOpt4} >Troca de Município de Veículo do RS</p>
            </div>
        </>
    )
}

export default Tela7
