import { Box, TableCell, TableHead, TableRow, TableSortLabel } from "@mui/material";
import { visuallyHidden } from '@mui/utils';

const headCells = [
    {
        id: 'veiculo',
        numeric: false,
        disablePadding: true,
        label: 'Veículo',
    },
    {
        id: 'placa',
        numeric: true,
        disablePadding: false,
        label: 'Placa',
    },
    {
        id: 'chassi',
        numeric: true,
        disablePadding: false,
        label: 'Chassi',
    },
    {
        id: 'proprietario',
        numeric: true,
        disablePadding: false,
        label: 'Proprietário',
    },
    {
        id: 'situacao',
        numeric: true,
        disablePadding: false,
        label: 'Situação'
    },
    {
        id: 'servico',
        numeric: true,
        disablePadding: false,
        label: 'Serviço',
    }
];
const headCellsMini = [
    {
        id: 'veiculo',
        numeric: false,
        disablePadding: false,
        label: 'Veículo',
    },
    {
        id: 'placa',
        numeric: true,
        disablePadding: false,
        label: 'Placa',
    },
];



export const TituloTabela = ({ order, orderBy, onRequestSort, largura }) => {
    const createSortHandler = (property) => (event) => {
        onRequestSort(event, property);
    };

    const valida = largura > 799 ? headCells : headCellsMini;

    return (
        <TableHead>
            <TableRow>
                { largura > 799 && <TableCell /> }
                {valida.map((headCell) => (
                    <TableCell
                        key={headCell.id}
                        align={headCell.numeric ? 'right' : 'left'}
                        padding={headCell.disablePadding ? 'none' : 'normal'}
                        sortDirection={orderBy === headCell.id ? order : false}
                    >
                        <TableSortLabel
                            active={orderBy === headCell.id}
                            direction={orderBy === headCell.id ? order : 'asc'}
                            onClick={createSortHandler(headCell.id)}
                        >
                            {headCell.label}
                            {orderBy === headCell.id ? (
                                <Box component="span" sx={visuallyHidden}>
                                    {order === 'desc' ? 'sorted descending' : 'sorted ascending'}
                                </Box>
                            ) : null}
                        </TableSortLabel>
                    </TableCell>
                ))}
            </TableRow>
        </TableHead>
    );
}
