import * as React from 'react';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';

const columns = [
  { field: 'nome', headerName: 'Nome', width: 280 },
  { field: 'rg', headerName: 'RG', width: 120 },
  { field: 'cpf', headerName: 'CPF', width: 120 },
  { field: 'tel', headerName: 'Telefone', width: 120 },
  { field: 'email', headerName: 'Email', width: 120 },
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

export function ListarUsuario() {


    const [usuario, setUsuario] = React.useState("");
    // const [itens, setItens] = React.useState("")
  
    React.useEffect(() => {
      axios.get('http://localhost:3001/Usuarios/Listar')
      .then(res => {
          setUsuario(res.data)
      })
      .catch(err => console.log(err))
    }, []);

  return (
    <div style={{ height: 400, width: '100%' }}>
      {usuario ? 
        <DataGrid
          rows={usuario}
          columns={columns}
          getRowId={(usuario) => usuario._id}
          editable={true}
          pageSize={5}
          rowsPerPageOptions={[5]}
          checkboxSelection
      />: null}
    </div>
  );
}




















