import { TextField } from "@mui/material"
import style from './Identificador.module.scss'

export const Identificador = ({ placa, setPlaca }) => {

    var passo1 = false
    console.log(placa.length)
    if (placa.length > 7) {
        passo1 = true
    }



    return (
        <>
            <div className={style.formulario}>
                <TextField
                    sx={{ minWidth: '30vw', margin: 2 }}
                    label='Placa'
                    placeholder='Digite a placa do veículo'
                    error={passo1}
                    helperText={passo1 ? "Placa inválida" : null}
                    value={placa}
                    onChange={(e) => {
                        setPlaca(e.target.value.toUpperCase());
                    }}
                    size='small'
                    required
                />
            </div>
        </>
    )
}
