import { useState } from 'react';
import { Button, TextField } from '@mui/material';
import style from './Login.module.scss'

const axios = require('axios').default;

export const Login = () => {

    const dt = new Date().toLocaleString()

    function loginRealizado(a) {
        if (a === "try") {
            document.getElementById('login--modal').showModal();
            document.getElementById('msg').innerHTML = "Verificando usuário e senha!"
        } else if (a === "successo") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = `Login realizado com sucesso! ${dt}`
            }, 3000)
        } else if (a === "incorreto") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Usuário ou senha incorreto."
            }, 3000)
        } else if (a === "erro") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Houve um erro ao tentar logar no servidor, tente novamente em alguns minutos."
            }, 3000)
        } else if (a === 'inativo') {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Usuário inativo ou bloqueado. Favor entrar em contato com o administrador do sistema."
            }, 3000)
        }
    }
    function fechaDiag() {
        let b = document.getElementById('msg').innerHTML
        if (b !== "Verificando usuário e senha!") {
            document.getElementById('login--modal').close();
        }
    }

    const LoginRealiza = () => {
        return (
            <>
                <dialog id='login--modal' onClick={fechaDiag}>
                    <div>
                        <p id='msg'></p>
                    </div>
                </dialog>
            </>
        )
    }

    const [login, setLogin] = useState();
    const [senha, setSenha] = useState();

    const Login = (e) => {
        e.preventDefault()
        loginRealizado("try")
        axios.post('http://192.168.15.49:3005/Login', {
            login: login,
            senha: senha
        })
            .then((response) => {
                if (response.status === 200) {
                    if ((!response.data) || (response.data === '')) {
                        loginRealizado("incorreto")
                    } else {
                        // console.log(response.data)
                        localStorage.setItem('user', JSON.stringify(response.data))
                    }
                }
            })
            .then(() => {
                let usuario = JSON.parse(localStorage.getItem('user'))
                axios.get('http://192.168.15.49:3005/UserLoading', {
                    params: { usuario }
                })
                    .then(res => {
                        // console.log(res.data[0].situacao)
                        // if (res.data[0].situacao === 'ativo') {
                            loginRealizado("successo")
                            localStorage.setItem('usuario', JSON.stringify(res.data[0]))
                            setTimeout(() => {
                                window.location.href = "Dashboard";
                            }, 5000)
                        // } else {
                        //     loginRealizado("inativo")
                        // }
                    })
                    .catch(err => console.log(err))
            })
            .catch((err) => {
                console.log(err);
                loginRealizado("erro")
            })
            .finally(() => {

                document.getElementById('login--form').reset()
            })
    }
    return (
        <>
            <div className={style.login}>
                <h1>Login</h1>
                <form id='login--form' className={style['login--form']} onSubmit={Login}>
                    <div className={style['login-user']}>
                        <TextField
                            id='login'
                            label='Usuário'
                            onChange={(e) => setLogin(e.target.value)}
                            color='success'
                            size='small'
                            fullWidth required />
                    </div>
                    <div className={style['login-user']}>
                        <TextField
                            id='senha'
                            label='Senha'
                            type='password'
                            onChange={(e) => setSenha(e.target.value)}
                            color='success'

                            size='small'
                            fullWidth required />
                    </div>
                    <div className={style['login-user']}>
                        <Button
                            variant='contained'
                            type='onSubmit'
                            color='success'
                            fullWidth>
                            Logar
                        </Button>
                    </div>
                    <p>Esqueci minha senha</p>
                </form>
                <LoginRealiza />
            </div>
            <div className={style['box--container']}>
                <h1>Confira os serviços disponíveis:</h1>
                <div>
                    <div>
                        <h2>Alterações de Documento</h2>
