import * as React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { TextField } from '@mui/material';

const steps = ['Identificação do Veículo','Cadastro do Veículo', 'Solicitação de Serviço', 'Revisão do Dados'];

const PlacaContext = React.createContext()

export const Service = () => {
    const [activeStep, setActiveStep] = React.useState(0);
    const [placa, setPlaca] = React.useState('')


  
    const handleNext = () => {
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };
  
    const handleBack = () => {
      setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };
  
    const handleReset = () => {
      setActiveStep(0);
    };

    const Identificador = () => {
        const {placa, setPlaca} = React.useContext(PlacaContext)
        console.log(placa)
        return(
            <>
                <TextField sx={{minWidth: '30vw', margin: 2}} label='Digite a placa do veículo' onChange={(e) => setPlaca(e.target.value.toUpperCase())} value={placa}/>
            </>
        )
    }
    const CadastroVeicular = (props) => {
        console.log(placa)
        return(
            <>
                <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
                <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
                <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
                <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
            </>
        )
    }
  
    return (
      <Box sx={{ width: '100%' }}>
        <Stepper activeStep={activeStep}>
          {steps.map((label) => {
            const stepProps = {};
            const labelProps = {};
            return (
              <Step key={label} {...stepProps}>
                <StepLabel {...labelProps}>{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        {activeStep === steps.length ? (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1 }}>
              Serviço solicitado com sucesso
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Box sx={{ flex: '1 1 auto' }} />
              <Button onClick={handleReset}>Resetar</Button>
            </Box>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1 }}>Etapa {activeStep + 1}</Typography>
            <Box sx={{display: 'flex', flexDirection: 'row', flexWrap: 'wrap', pr: 2 , justifyContent: 'space-around'}} >
                <PlacaContext.Provider value={{placa, setPlaca}}>
                {activeStep === 0 ? <Identificador placa={placa} setPlaca={setPlaca} /> : null}

                {activeStep === 1 ? <CadastroVeicular placa={placa} /> : null}
                </PlacaContext.Provider>
            </Box>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Button
                color="inherit"
                disabled={activeStep === 0}
                onClick={handleBack}
                sx={{ mr: 1 }}
              >
                Voltar
              </Button>
              <Box sx={{ flex: '1 1 auto' }} />
  
              <Button color='success' onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finalizar' : 'Próximo'}
              </Button>
            </Box>
          </React.Fragment>
        )}
      </Box>
    );
}
