import React from "react";
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TableCell, TableRow } from "@mui/material"



export const Linha = ({ row, labelId }) => {

    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleConfirm = () => {
        axios.post('http://localhost:3001/GerarGRT')
    }

    const EnviarGRT = () => {
        console.log(row)

    }

    return (
        <>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="Validar"
                aria-describedby="Validar-description"
            >
                <DialogTitle id="Validar">
                    {row.quantidade > 1 ? "Validar as GRTs" : "Validar a GRT"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Validar-description">
                        {row.quantidade > 1 ? `Ser치 liberado ${row.quantidade} GRTs ao despachante ${row.nome}` : `Ser치 liberado 1 GRT ao despachante ${row.nome}`}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button color='success' onClick={handleConfirm} >Confirmar</Button>
                    <Button color='error' onClick={handleClose} >Cancelar</Button>
                </DialogActions>
            </Dialog>
            <TableRow
                key={row._id}
                hover
            >
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
                <TableCell
                    component="th"
                    id={labelId}
                    scope="row"
                    padding="none"
                >
                    {row.nome}
                </TableCell>
                <TableCell align="right">{row.data}</TableCell>
                <TableCell align="right">{row.hora}</TableCell>
                <TableCell align="right">{row.quantidade}</TableCell>
                <TableCell align="right">{row.situacao}</TableCell>
                <TableCell>
                    <Button
                        onClick={handleClickOpen}
                    >
                        enviar grt
                    </Button>
                </TableCell>
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
            </TableRow>
        </>
    )
}
