import * as React from 'react';
import Box from '@mui/material/Box';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import IconButton from '@mui/material/IconButton';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';
import style from './ListarUsuario.module.scss'
import { InputBase, Typography } from '@mui/material';
import { Linha } from './Linha/Linha';
import { TituloTabela } from './TituloTabela/TituloTabela';

function descendingComparator(a, b, orderBy) {
    if (b[orderBy] < a[orderBy]) {
        return -1;
    }
    if (b[orderBy] > a[orderBy]) {
        return 1;
    }
    return 0;
}

function getComparator(order, orderBy) {
    return order === 'desc'
        ? (a, b) => descendingComparator(a, b, orderBy)
        : (a, b) => -descendingComparator(a, b, orderBy);
}

function stableSort(array, comparator) {
    const stabilizedThis = array.map((el) => [el, el._id]);
    stabilizedThis.sort((a, b) => {
        const order = comparator(a[0], b[0]);
        if (order !== 0) {
            return order;
        }
        return a[1] - b[1];
    });
    return stabilizedThis.map((el) => el[0]);
}

export function ListarUsuario({ editUser, setId, largura, user }) {
    const [order, setOrder] = React.useState('asc');
    const [orderBy, setOrderBy] = React.useState('_id');
    const [page, setPage] = React.useState(0);
    const [dense, setDense] = React.useState(false);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [linha2, setLinha2] = React.useState()
    const [itens, setItens] = React.useState("")
    const [teste, setTeste] = React.useState('')
    const [ver, setVer] = React.useState()

    React.useEffect(() => {
        axios.get('http://192.168.15.49:3005/Usuarios/Listar', {
            params: { user }
        })
            .then(res => {
                setLinha2(res.data)
            })
            .catch(err => console.log(err))
    }, []);

    const handleRequestSort = (event, property) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
    };

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handleChangeDense = (event) => {
        setDense(event.target.checked);
    };

    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - teste.length) : 0;

    function DeleteUser() {
        let { _id } = itens
        axios.post('http://192.168.15.49:3005/Usuario/Deletar', { _id })
            .then(res => {
                if (res.data.deletedCount === 0) { return; }
                else {
                    axios.get('http://192.168.15.49:3005/Usuarios/Listar', {
                        params: { user }
                    })
                        .then(res => setTeste(res.data))
                        .catch(err => console.log(err))
                }
            })
            .catch(err => console.log(err))
    }
    React.useEffect(() => {
        function testaBusca(title) {
            const regex = new RegExp(ver, 'i');
            return regex.test(title);
        }
        if ((linha2 === undefined) || (linha2 === '')) { } else {
            let tentativa = linha2.filter(a => testaBusca(a.nome) || testaBusca(a.rg) || testaBusca(a.cpf) || testaBusca(a.tel) || testaBusca(a.email) || testaBusca(a.usuariotipo))
            setTeste(tentativa)
        }
    }, [ver, linha2]);

    return (
        <>
            {teste ?
                <Box sx={{ width: '100%' }}>
                    <Paper sx={{ width: '100%', mb: 2, pt: 3 }}>
                        <Paper
                            elevation={6}
                            component="div"
                            sx={{ p: '2px 4px', m: '0px auto 10px auto', display: 'flex', alignItems: 'center', maxWidth: 400, minWidth: 100 }}
                        >

                            <InputBase
                                sx={{ ml: 1, flex: 1 }}
                                placeholder="Busca"
                                inputProps={{ 'aria-label': 'busca' }}
                                onChange={(e) => setVer(e.target.value)}
                            />
                            <IconButton>
                                <SearchIcon />
                            </IconButton>
                        </Paper>
                        <TableContainer>
                            <Table
                                sx={{ minWidth: 200 }}
                                aria-labelledby="tableTitle"
                                size={dense ? 'small' : 'medium'}
                            >
                                <TituloTabela
                                    order={order}
                                    orderBy={orderBy}
                                    onRequestSort={handleRequestSort}
                                    largura={largura}
                                />
                                <TableBody>
                                    {stableSort(teste, getComparator(order, orderBy))
                                        .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                        .map((row) => {
                                            return (
                                                <Linha
                                                    key={row._id}
                                                    setItens={setItens}
                                                    setId={setId}
                                                    editUser={editUser}
                                                    row={row}
                                                    dense={dense}
                                                    DeleteUser={DeleteUser}
                                                    largura={largura}
                                                />
                                            );
                                        })}
                                    {emptyRows > 0 && (
                                        <TableRow
                                            style={{
                                                height: (dense ? 33 : 53) * emptyRows,
                                            }}
                                        >
                                            <TableCell colSpan={6} />
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[10, 25, 50, 100, 1000]}
                            component="div"
                            count={teste.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </Paper>
                    <FormControlLabel
                        control={<Switch checked={dense} onChange={handleChangeDense} />}
                        label="Modo compacto"
                    />
                    <div className={style.legenda}>
                        <p></p>
                        <p></p>
                        <p></p>
                        <p>Inativo</p>
                    </div>

                </Box> : <p>Loading...</p>
            }

        </>
    );
}
