import * as React from 'react';
import { Box, Button, InputLabel, MenuItem, Paper, Select } from "@mui/material"
import axios from "axios"
import { useState } from "react"
import style from './UploadArquivo.module.scss'

export const UploadArquivo = ({ user, veiculo }) => {

    const [imagens, setImagens] = React.useState([])
    const [imagensURLs, setImagensURLs] = React.useState([])

    console.log(imagens)

    React.useEffect(() => {
        if (imagens.length < 1) return;
        const novaImagensURLs = [];
        imagens.forEach(imagem => novaImagensURLs.push(URL.createObjectURL(imagem)));
        setImagensURLs(novaImagensURLs);
    }, [imagens])

    const onImagens = (e) => {
        console.log(e.target.files)
        setImagens([...e.currentTarget.files])
    }

    const [retorno, setRetorno] = React.useState()
    const [grt, setGrt] = React.useState("")

    const escolheGrt = (e) => {
        setGrt(e.target.value)
    }

    React.useEffect(() => {
        axios.get("http://localhost:3001/GRT/Utilizaveis", {
            params: { user }
        })
            .then(res => {
                console.log(res.data)
                setRetorno(res.data)
            })
            .catch(err => console.log(err))
    }, [])


    const salvarGrt = (e) => {
        e.preventDefault()
        // axios.post('http://localhost:3001/Servico/GravaGrt', {user, veiculo, grt, })
        // .then(res => {
        //     console.log(res)
        // })
        // .catch(err => console.log(err))
    }

    // console.log(user, retorno, grt, veiculo)

    return (
        <>
            <Paper sx={{ p: 2 }}>
                <Box sx={{ width: '100%' }}>
                    <form className={style.container} onSubmit={salvarGrt}>
                        <div className={style.botaoUp}>
                            <InputLabel id="selecionaGrt">Selecione a GRT</InputLabel>
                            <Select
                                labelId="selecionaGrt"
                                id="SelGRT"
                                value={grt}
                                label='Selecione a GRT'
                                onChange={escolheGrt}
                            >
                                {/* <MenuItem value={10}>dez</MenuItem>
                                <MenuItem value={20}>vinte</MenuItem> */}
                                {retorno !== undefined ? retorno.map((iten) => {
                                    return (
                                        <MenuItem key={iten.id} value={iten.GRT}>{iten.GRT}</MenuItem>
                                    )
                                }) : null}
                            </Select>
                        </div>
                        <div className={style.boxConteiner}>
                        <Button sx={{ m: 2 }} variant="contained" component="label">
                            Enviar rg
                            <input hidden accept="image/*" multiple type="file" name='teste' label='ops' onChange={onImagens} />
                        </Button>
                            <div className={style.amostra}>
                                {imagens.length === 0 ? <p>Aguardando Arquivo</p> : imagensURLs.map((imagemSrc, index) => <img key={index} src={imagemSrc} alt='' />)}
                            </div>
                        </div>

                        <div className={style.botao}>
                            <Button color='success' type="submit">Salvar</Button>
                        </div>

                    </form>
                </Box>
            </Paper>
        </>
    )
}
