import { TextField } from "@mui/material"
import axios from "axios"
import React from "react"


export const CadastroVeicular = ({placa}) => {
    const [info, setInfo] = React.useState('')

    console.log(placa)

    React.useEffect(() => {
        axios.get('http://localhost:3001/Servicos', {
            params: { placa }
        })
        .then(res => {
            if(res) {
                setInfo(res.data.RESPOSTA.BASE_AGREGADOS)
            } else {
                console.log('Erro:', res)
            }
        })
        .catch(err => console.log(err))
    },[placa])
    
    console.log(info)

    const [dado, setDado] = React.useState({
        veiculo: '',
        marca: '',
    })

    const padrao = {
        minWidth: '30vw',
        margin: 2,
    }

    return(
        <>
            <TextField sx={padrao} label='Veículo' value={dado.veiculo} onChange={(e) => setDado(a => ({...a, veiculo: e.target.value}))}/>
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
        </>
    )
}
