import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import { CadastroUsuario } from './Usuario/Cadastro/CadastroUsuario';
import { ListarUsuario } from './Usuario/Listar/ListarUsuario';
import { EditarUsuario } from './Usuario/Editar/EditarUsuario';
import { Service } from './Servico/Servico';
import { ListarServico } from './Servico/ListarServico/ListarServico';



const Dashboard = () => {

    const loginUser = JSON.parse(localStorage.getItem('usuario'))

    const [activeStep, setActiveStep] = React.useState(0);

    React.useEffect(() => {
        console.log(loginUser)
    }, [])

    const largura = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;

    const outdoor = [
        "Tela Inicial",
        "Cadastro de Usuário",
        "Lista de Usuário",
        "Editar Usuário",
        "Solicitar Serviço",
        "Lista de Serviço",
        "Solicitar GRT",
        "Listar GRT"
    ]

    const [tela, setTela] = useState({
        letreiro: outdoor[0],
        inicial: true,
        useradd: false,
        userlist: false,
        userEdit: false,
        service: false,
        servlist: false,
        grtadd: false,
        grtlist: false
    })
    const [id, setId] = useState('')
    const [open, setOpen] = useState({
        bcadastro: false,
        bservico: false,
        bgrt: false
    })


    const openUser = () => { setOpen(a => ({ ...a, bcadastro: !open.bcadastro, bservico: false, bgrt: false })) }
    const openService = () => { setOpen(a => ({ ...a, bcadastro: false, bservico: !open.bservico, bgrt: false })) }
    const openGrt = () => { setOpen(a => ({ ...a, bcadastro: false, bservico: false, bgrt: !open.bgrt }) ) }
    const telaInicial = () => { setTela(a => ({ ...a, inicial: true, useradd: false, userlist: false, userView: false, userEdit: false, service: false, servlist: false, grtadd: false, grtlist: false, letreiro: outdoor[0] })) }
    const addUser = () => { setTela(a => ({ ...a, inicial: false, useradd: true, userlist: false, userView: false, userEdit: false, service: false, servlist: false, grtadd: false, grtlist: false, letreiro: outdoor[1] })) }
    const listUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: true, userView: false, userEdit: false, service: false, servlist: false, grtadd: false, grtlist: false, letreiro: outdoor[2] })) }
    const editUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: true, service: false, servlist: false, grtadd: false, grtlist: false, letreiro: outdoor[3] })) }
    const serVice = () => { console.log("click");  setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: false, service: true, servlist: false, grtadd: false, grtlist: false, letreiro: outdoor[4] })) }
    const listServ = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: false, service: false, servlist: true, grtadd: false, grtlist: false, letreiro: outdoor[5] })) }
    const addGrt = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: false, service: false, servlist: false, grtadd: true, grtlist: false, letreiro: outdoor[6] })) }
    const listGrt = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: false, service: false, servlist: false, grtadd: false, grtlist: true, letreiro: outdoor[7] })) }


    // console.log(open)

    return (
        <>
            <div className={style.cabeca}>
                <Cabecalho />
                <div className={style.containerFundo}>
                    <div className={style.info}>
                        <p>teste</p>
                        <p>{tela.letreiro}</p>
                        <p>teste</p>
                    </div>
                    <div className={style.container}>
                        <aside className={style.lateral}>
                            <List
                                sx={{ width: '100%', bgcolor: 'background.paper' }}
                                dense={true}
                                component="nav"
                                aria-labelledby="nested-list-subheader"
                                subheader={
                                    <ListSubheader component="div" id="nested-list-subheader">
                                        <p>Menu</p>
                                    </ListSubheader>
                                }
                            >
                                <ListItemButton onClick={telaInicial}>
                                    <ListItemIcon>
                                        <HomeIcon />
                                    </ListItemIcon>
                                    <ListItemText primary="Tela inicial" />
                                </ListItemButton>
                                {loginUser.usuariotipo !== 'preposto' ?
                                    <>
                                        <ListItemButton onClick={openUser}>
                                            <ListItemIcon>
                                                <AccountCircleIcon />
                                            </ListItemIcon>
                                            <ListItemText primary="Usuário" />
                                            {open.bcadastro ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        <Collapse in={open.bcadastro} timeout='auto' unmountOnExit>
                                            <List dense={true} component='div' >
                                                <ListItemButton sx={{ pl: 4 }} onClick={addUser}>
                                                    <ListItemIcon>
                                                        <PersonAddIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Cadastrar Usuário' />
                                                </ListItemButton>
                                                <ListItemButton sx={{ pl: 4 }} onClick={listUser}>
                                                    <ListItemIcon>
                                                        <GroupIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Listar Usuário' />
                                                </ListItemButton>
                                            </List>
                                        </Collapse>
                                    </>
                                : null}

                                {loginUser.usuariotipo !== 'master' ? loginUser.usuariotipo !== 'administrador' ?
                                    <>
                                        <ListItemButton onClick={openService}>
                                            <ListItemIcon>
                                                <CommuteIcon />
                                            </ListItemIcon>
                                            <ListItemText primary="Serviço" />
                                            {open.bservico ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        <Collapse in={open.bservico} timeout='auto' unmountOnExit>
                                            <List dense={true} component='div' >
                                                <ListItemButton sx={{ pl: 4 }} onClick={serVice}>
                                                    <ListItemIcon>
                                                        <PostAddIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Solicitar Serviço' />
                                                </ListItemButton>
                                                <ListItemButton sx={{ pl: 4 }} onClick={listServ}>
                                                    <ListItemIcon>
                                                        <AssignmentIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Listar Serviços' />
                                                </ListItemButton>
                                            </List>
                                        </Collapse>
                                    </>
                                    : null : null}

                                    
                                {loginUser.usuariotipo === 'despachante' ?
                                    <>
                                        <ListItemButton onClick={openGrt}>
                                            <ListItemIcon>
                                                <DocumentScannerIcon />
                                            </ListItemIcon>
                                            <ListItemText primary="GRT" />
                                            {open.bgrt ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        <Collapse in={open.bgrt} timeout='auto' unmountOnExit>
                                            <List dense={true} component='div' >
                                                <ListItemButton sx={{ pl: 4 }} onClick={addUser}>
                                                    <ListItemIcon>
                                                        <AddBoxIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Solicitar GRT' />
                                                </ListItemButton>
                                                <ListItemButton sx={{ pl: 4 }} onClick={listUser}>
                                                    <ListItemIcon>
                                                        <FormatListNumberedRtlIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Listar GRT' />
                                                </ListItemButton>
                                            </List>
                                        </Collapse>
                                    </>
                                : null}

                                <ListItemButton onClick={() => {
                                    localStorage.clear('usuario')
                                    window.location.href = "/"
                                }}>
                                    <ListItemIcon>
                                        <KeyboardReturnIcon />
                                    </ListItemIcon>
                                    <ListItemText primary="Sair" />
                                </ListItemButton>
                            </List>
                        </aside>
                        <div className={style.boxCentral}>
                            <div className={style.boxCentralContainer}>
                                {tela.inicial ? <TelaInicial user={loginUser.nome} /> : null}
                                {tela.useradd ? <CadastroUsuario user={loginUser} /> : null}
                                {tela.userlist ? <ListarUsuario user={loginUser} setId={setId} editUser={editUser} largura={largura} /> : null}
                                {tela.userEdit ? <EditarUsuario id={id} listUser={listUser} /> : null}
                                {tela.servlist ? <ListarServico user={loginUser} largura={largura} serVice={serVice} setActiveStep={setActiveStep} /> : null }
                                {tela.service ? <Service loginUser={loginUser} setActiveStep={setActiveStep} activeStep={activeStep} /> : null}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Dashboard;
