import { TextField } from "@mui/material"
import axios from "axios"
import React from "react"


export const CadastroVeicular = ({placa}) => {
    const [dado, setDado] = React.useState({
        veiculo: '',
        marca: '',
    })

    React.useEffect(() => {
        axios.get('http://localhost:3001/Servicos', {
            params: placa
        })
    }, [placa])

    console.log(dado)

    return(
        <>
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' value={dado.veiculo} onChange={(e) => setDado(a => ({...a, veiculo: e.target.value}))}/>
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
            <TextField sx={{minWidth: '30vw', margin: 2}} label='Veículo' />
