import * as React from 'react';
import axios from 'axios';
import style from './Inicial.module.scss'

export const TelaInicial = ({ user }) => {

    const [resposta, setResposta] = React.useState()

    const { nome } = user

    React.useEffect(() => {
        axios.get("http://localhost:3001/GRT/Disponiveis", {
            params: { nome }
        })
            .then(res => {
                // console.log("Foi aqui:", res.data);
                setResposta(res.data.length)
            })
            .catch(err => {
                // console.log(err);
            })
    }, [nome])
    // console.log(user)
    const d = new Date();
    const dt = Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)

    return (
        <div className={style["tela--inicial"]}>
            <p>CRDD-RS</p>
            <p>{dt}</p>
            <p>Seja bem vindo, {user.nome}.</p><br/>
            {user.usuariotipo === "master" || user.usuariotipo === "administrador" ? null :
                <h2><p>GRTs dispon√≠veis:</p> {resposta <= 10 ? <p style={{color: 'red'}} >{resposta} </p> : <p>{resposta} </p>}</h2>
            }
        </div>
    )
}
