import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, Paper, Typography } from "@mui/material"
import { Box } from "@mui/system"
import AddIcon from '@mui/icons-material/Add';
import HorizontalRuleIcon from '@mui/icons-material/HorizontalRule';
import { useState } from "react";

export const SolicitarGrt = ({ loginUser }) => {


    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const [qtd, setQtd] = useState({
        valor: 1.50,
        quantidade: 1
    })

    const valorPadrão = 1.50
    var valida = false

    const Add = () => {
        setQtd(a => ({ ...a, valor: qtd.valor + 1.50, quantidade: qtd.quantidade + 1 }))
    }

    const Sub = () => {
        setQtd(a => ({ ...a, valor: qtd.valor - 1.50, quantidade: qtd.quantidade - 1 }))
    }

    qtd.valor === 1.50 ? valida = true : valida = false

    return (
        <>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    {"Use Google's location service?"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Let Google help apps determine location. This means sending anonymous
                        location data to Google, even when no apps are running.
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Disagree</Button>
                    <Button onClick={handleClose} autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>
            <Typography variant='h6' color='primary'> Solicitar GRT</Typography>
            <Box sx={{ width: '100%' }}>
                <Paper sx={{ p: 4, m: 2 }} elevation={6} >
                    <Typography variant="h5">Valor por GRT: R$ {valorPadrão.toFixed(2)} </Typography>
                    <br /><br />
                    <Typography sx={{ p: 2 }} >Valor total da GRT: R$ {qtd.quantidade} </Typography>
                    <Typography sx={{ p: 2 }} >Valor total da GRT: R$ {qtd.valor.toFixed(2)} </Typography>

                    <Box>
                        <IconButton onClick={Add}>
                            <AddIcon />
                        </IconButton>
                        <IconButton id='minus' onClick={Sub} disabled={valida} >
                            <HorizontalRuleIcon />
                        </IconButton>
                    </Box>
                    <br /><br />
                    <Button sx={{ left: 1 }} onClick={handleClickOpen} >Solicitar</Button>
                </Paper>
            </Box>
        </>
    )
}
