import { TextField } from "@mui/material"
import axios from "axios"
import React from "react"


export const CadastroVeicular = ({placa}) => {
    const [info, setInfo] = React.useState({
        veiculo: '',
        modelo: '',
    })

    console.log(placa)

    React.useEffect(() => {
        axios.get('http://localhost:3001/Servicos', {
            params: { placa }
        })
        .then(res => {
            if(res) {
                let teste = res.data.RESPOSTA.BASE_AGREGADOS;
                console.log(teste)
                setInfo(a => ({...a, 
                    veiculo: teste.Grupo_Modelo,
                }))

            } else {
                console.log('Erro:', res)
            }
        })
        .catch(err => console.log(err))
    },[placa])
    
    console.log(info)

    const padrao = {
        minWidth: '30vw',
        margin: 1.5,
    }

    return(
        <>
        {info !== '' ? 
        <>
            <TextField sx={padrao} size='small' label='Veículo' value={info.veiculo} onChange={(e) => setInfo(a => ({...a, veiculo: e.target.value}))}/>
            <TextField sx={padrao} size='small' label='Veículo' />
            <TextField sx={padrao} size='small' label='Veículo' />
            <TextField sx={padrao} size='small' label='Veículo' />
        </>
        : <p>Loading</p>}
        </>
    )
}
