import { TextField } from "@mui/material"

export const Identificador = ({ placa, setPlaca }) => {

    var valida = false;

    console.log(placa.length)
    if (placa.length > 7) {
        valida = true
    }

    return (
        <>
            <div className="formulario">
                <TextField
                    sx={{ minWidth: '30vw', margin: 2 }}
                    label='Placa'
                    placeholder='Digite a placa do veÃ­culo'
                    error={valida}
                    helperText={valida ? "Placa deve conter no MAXIMO 7 caracteres" : null}
                    value={placa}
                    onChange={(e) => setPlaca(e.target.value.toUpperCase())}
                    size='small'
                    required
                />
            </div>
        </>
    )
}
