sourcesContent:
import React from "react";
import { Button, Checkbox, FormControl, FormControlLabel, FormLabel, Input, InputAdornment, Paper, Radio, RadioGroup, TextField, Typography } from "@mui/material"
import style from './SolicitaServico.module.scss';
import { Box } from "@mui/system";

export const SolicitaServico = ({ setSele, user, veiculo }) => {

    const service = [
        {
            id: '1',
            label: '2ª via do CRV'
        }, {
            id: '2',
            label: 'Impressão do CRLVe'
        }, {
            id: '3',
            label: 'Emissão de Certidão'
        }, {
            id: '4',
            label: 'Cópia de documentos'
        }, {
            id: '5',
            label: 'Licença Especial de Trânsito'
        }, {
            id: '6',
            label: 'Alteração de Informações do Proprietário'
        }, {
            id: '7',
            label: 'Alteração de Informações do Veículo'
        }, {
            id: '8',
            label: 'Inclusão de Restrição Financeira'
        }, {
            id: '9',
            label: 'Alteração de Restrição Financeira'
        }, {
            id: '10',
            label: 'Liberação de Restrição Financeira'
        }, {
            id: '11',
            label: 'Inclusão de Averbação de Execução'
        }, {
            id: '12',
            label: 'Liberação de Averbação de Execução'
        }, {
            id: '13',
            label: 'Restrição por Transferência'
        }, {
            id: '14',
            label: 'Inclusão Placa de Experiência ou de Fabricante'
        }, {
            id: '15',
            label: 'Renovação de Placa de Experiência ou de Fabricante'
        }, {
            id: '16',
            label: 'Baixa de Placa de Experiência ou de Fabricante'
        }, {
            id: '17',
            label: 'Baixa para outra UF'
        }, {
            id: '18',
            label: '2ª via do CRV'
        }, {
            id: '19',
            label: 'Baixa para militarização'
        }, {
            id: '20',
            label: 'Baixa para outro país'
        }, {
            id: '21',
            label: 'Cancelamento de Processo'
        }, {
            id: '22',
            label: 'Comunicação de Venda'
        }, {
            id: '23',
            label: 'Correção das Observações do CRLV-e'
        }, {
            id: '24',
            label: 'Correção do Chassi'
        }, {
            id: '25',
            label: 'Correção de Informações do Proprietário'
        }, {
            id: '26',
            label: 'Correção de Infomrações do Veículo'
        }, {
            id: '27',
            label: 'Correção de Município'
        }, {
            id: '28',
            label: 'Correção de Restrições'
        }, {
            id: '29',
            label: 'Mudança p/ Placa Única / Troca de Placa Mercosul'
        }, {
            id: '30',
            label: 'Primeiro Emplacamento'
        }, {
            id: '31',
            label: 'Reserva de Placa'
        }, {
            id: '32',
            label: 'Autorização para Fabricação de Placas'
        }, {
            id: '33',
            label: 'Colocação de lacre em placa'
        }, {
            id: '34',
            label: 'Autorização Para alteração de Características'
        }, {
            id: '35',
            label: 'Autorização para Regravação de Nº de Chassi'
        }, {
            id: '36',
            label: 'Autorização para Regravação de Nº de Motor'
        }, {
            id: '37',
            label: 'Autorização para Transporte Escolar'
        }, {
            id: '38',
            label: 'Solicitação de Vistoria'
        }, {
            id: '39',
            label: 'Transferência de Município de Veículo de Outra UF'
        }, {
            id: '40',
            label: 'Transferência de Município de Veículo do RS'
        }, {
            id: '41',
            label: 'Troca de Município de Veículo de Outra UF'
        }, {
            id: '42',
            label: 'Troca de Município de Veículo do RS'
        }
    ];

    const outros = {
        id: '43',
        label: 'Outros'
    };

    const finan1 = [
        {
            id: '1',
            label: 'Arrendamento',
        }, {
            id: '2',
            label: 'Penhor'
        }
    ];

    const finan2 = [
        {
            id: '3',
            label: 'Reserva de Domínio'
        }, {
            id: '4',
            label: 'Comodato',
        }, {
            id: '5',
            label: 'Pessoa Física'
        }
    ];

    const finan3 = [
        {
            id: '6',
            label: 'Alienação Fiduciária',
        }, {
            id: '7',
            label: 'Locação',
        }, {
            id: '8',
            label: 'Pessoa Jurídica'
        }
    ];

    const verificaAverdade = {
        verdade: true,
        falsidade: false
    }

    const [selectedIndex, setSelectedIndex] = React.useState(0);
    const [serv, setServ] = React.useState('');
    const [finan, setFinan] = React.useState('nao');
    const [financeiro, setFinanceiro] = React.useState('');
    const [motivoplaca, setMotivoPlaca] = React.useState('');
    const [motivolacre, setMotivoLacre] = React.useState('');
    const [selecend, setSelecEnd] = React.useState(false);
    const [credor, setCredor] = React.useState('');
    const [telefone, setTelefone] = React.useState('');
    const [email, setEmail] = React.useState('')
    const [agente, setAgente] = React.useState('')
    const [selecionaEndereco, setSelecionaEndereco] = React.useState('')
    const [confirmaendereco, setConfirmaEndereco] = React.useState('')
    const [confirmacontato, setConfirmaContato] = React.useState('')
    const [ data, setData ] = React.useState({
        dia: '',
        mes: '',
        ano: ''
    })
    const [servico, setServico] = React.useState({
        nome: user.nome,
        rg: user.rg,
        cpf: user.cpf,
        telefone: user.tel,
        placa: veiculo.placa,
        chassi: veiculo.chassi,
        servico: '',
        restricaoFinanceira: '',
        restricao: '',
        credor: '',
        agente: '',
        endereço: '',
        alteração: '',
        telefoneProprietario: '',
        emailProprietario: '',
        autorizacao: '',
        assinatura: {
            municipio: '',
            dia: '',
            mes: '',
            ano: '',
        }
    })

    const handleListItemClick = (event, index) => {
        setSelectedIndex(index);
        setSele(true)
    };

    const SelecionaServico = (event) => {
        setServ(event.target.value);
        setMotivoLacre('')
        setMotivoPlaca('')
    };

    const FinanSelect = (e) => {
        e.target.value === 'nao' && setFinanceiro(''); setCredor(''); setAgente('');
        setFinan(e.target.value);
    }

    const SelecionaFinanceiro = (e) => {
        setFinanceiro(e.target.value)
    }

    const MotivoPlaca = (e) => {
        setMotivoPlaca(e.target.value)
    }

    const MotivoLacre = (e) => {
        setMotivoLacre(e.target.value)
    }

    const SelecionaEndereco = (e) => {
        setSelecionaEndereco(e.target.checked === true ? 'sim' : 'nao')
    }

    const AlteraEndereco = (e) => {
        setConfirmaEndereco(e.target.value)
    }

    const AutorizaEndereco = (e) => {
        setConfirmaContato(e.target.value)
    }


    const look = finan === 'nao' ? verificaAverdade.verdade : verificaAverdade.falsidade
    
    const servicoSolicitado = serv !== '' ? service[serv].label : null;

    var servicoFinenceiro;

    var valida = true

    if (serv === "43") {
        valida = false
    } else {
        valida = true
    }

    if ( financeiro !== '') {
        if (financeiro < 3) {
            servicoFinenceiro = finan1[financeiro].label
        } else if ((financeiro > 2) && (financeiro < 6)) {
            servicoFinenceiro = finan2[financeiro].label
        } else if ((financeiro > 5) && (financeiro < 9)) {
            servicoFinenceiro = finan3[financeiro].label
        } else {
            servicoFinenceiro = '';
        }
    } 

    console.log('foi', serv, financeiro, valida)

    console.log(finan)

    console.log(motivolacre, motivoplaca)

    console.log(selecend, credor, telefone, email, agente)

    console.log(servico, servicoSolicitado, selecionaEndereco)

    console.log(confirmaendereco)

    const SolServico = (e) => {
        e.preventDefault()
        setServico(a => ({...a,
            servico: servicoSolicitado,
            restricaoFinanceira: finan,
            restricao: servicoFinenceiro,
            credor: credor,
            agente: agente,
            endereço: selecionaEndereco,
            alteração: confirmaendereco,
            telefoneProprietario: telefone,
            emailProprietario: email,
            autorizacao: confirmacontato,
            assinatura: {
                municipio: '',
                dia: '',
                mes: '',
                ano: '',
            }
        }))
        
    console.log(servico)
    }
    return (
        <>
            <Typography variant='h6'>Selecione o serviço desejado</Typography>
            <div className={style.opcoes}>
                <Box sx={{ width: '100%' }}>
                    <Paper sx={{ p: 2 }}>
                        <Box sx={{ width: '100%' }}>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>Eu, </Typography>
                                <div className={style.nome}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={user.nome}
                                            sx={{ width: '65vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6' noWrap={true}>, Identidade</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>nº</Typography>
                                <div className={style.rg}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={user.rg}
                                            sx={{ width: '30vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6' noWrap={true}>, CPF nº</Typography>
                                <div className={style.cpf}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={user.cpf}
                                            sx={{ width: '30vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6' noWrap={true}>, Telefone</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6'>nº</Typography>
                                <div className={style.telefone}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={user.tel}
                                            sx={{ width: '35vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6' noWrap={true}>, requeiro ao DETRAN/RS a realização do seguinte serviço,</Typography>
                            </div>
                            <div className={style.identificacao}>
                                <Typography variant='h6' noWrap={true} >Relato ao(s) veículos(s) placa(s)</Typography>
                                <div className={style.placa}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={veiculo.placa}
                                            sx={{ width: '23vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6' noWrap={true} >, chassi(s)</Typography>
                                <div className={style.chassi}>
                                    <FormControl fullWidth variant="standard">
                                        <Input
                                            id="standard-adornment-amount"
                                            value={veiculo.chassi}
                                            sx={{ width: '23vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                        />
                                    </FormControl>
                                </div>
                                <Typography variant='h6'>:</Typography>
                            </div>
                        </Box> <br />
                        <form onSubmit={SolServico}>
                            <Box sx={{ width: '100%' }}>
                                <div className={style.selecionaServico}>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={serv}
                                            onChange={SelecionaServico}
                                        >
                                            {[...Array(23)].map((iten, i) => (
                                                <FormControlLabel key={i + 1} index={i + 1} value={service[i].id} control={<Radio size="small" />} label={service[i].label} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={serv}
                                            onChange={SelecionaServico}
                                        >
                                            {service.map((iten, i) => i > 22 && (
                                                <>
                                                    <FormControlLabel key={i + 1} index={i + 1} value={service[i].id} control={<Radio size='small' />} label={service[i].label} />
                                                    {service[i].id === '32' ?
                                                        <RadioGroup
                                                            defaultValue=""
                                                            name="select-service"
                                                            value={motivoplaca}
                                                            onChange={MotivoPlaca}
                                                        >
                                                            <div className={style.opcional}>
                                                                <FormControlLabel value='Furto/Roubo' control={<Radio size="small" />} label='furto/roubo' disabled={serv === '32' ? false : true} />
                                                                <FormControlLabel value='Perda/Extravio' control={<Radio size="small" />} label='perda/extravio' disabled={serv === '32' ? false : true} />
                                                                <FormControlLabel value='Outros' control={<Radio size="small" />} label='outros' disabled={serv === '32' ? false : true} />
                                                            </div>
                                                        </RadioGroup>
                                                        :
                                                        null
                                                    }
                                                    {service[i].id === '33' ?
                                                        <RadioGroup
                                                            defaultValue=""
                                                            name="select-service"
                                                            value={motivolacre}
                                                            onChange={MotivoLacre}
                                                        >
                                                            <div className={style.opcional}>
                                                                <FormControlLabel value='Furto/Roubo' control={<Radio size="small" />} label='furto/roubo' disabled={serv === '33' ? false : true} />
                                                                <FormControlLabel value='Perda/Extravio' control={<Radio size="small" />} label='perda/extravio' disabled={serv === '33' ? false : true} />
                                                                <FormControlLabel value='Outros' control={<Radio size="small" />} label='outros' disabled={serv === '33' ? false : true} />
                                                            </div>
                                                        </RadioGroup>
                                                        :
                                                        null
                                                    }
                                                </>
                                            ))}
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className={style.outro}>
                                    <RadioGroup
                                        defaultValue=""
                                        name="select-service"
                                        value={serv}
                                        onChange={SelecionaServico}
                                    >
                                        <FormControlLabel value={outros.id} control={<Radio size="small" />} label={outros.label} />
                                    </RadioGroup>
                                    <TextField variant="standard" size="small" disabled={valida} fullWidth valeu={serv} onChange={(e) => setServ(e.target.value)} />
                                </div>
                            </Box>
                            <Box sx={{ width: '100%' }}>
                                <div className={style.outro}>
                                    <Typography variant='h6'>Haverá inclusão ou alteração de restrição financeira? </Typography>
                                    <RadioGroup
                                        sx={{ ml: 2 }}
                                        defaultValue=""
                                        name="select-service"
                                        value={finan}
                                        onChange={FinanSelect}
                                    >
                                        <div className={style.outro}>
                                            <FormControlLabel value='sim' control={<Radio size="small" />} label={'Sim'} />
                                            <FormControlLabel value='nao' control={<Radio size="small" />} label={'Não'} />
                                        </div>
                                    </RadioGroup>
                                </div>
                                <div className={style.finan}>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={financeiro}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan1.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                        <div className={style.outro}>
                                            <Typography noWrap>O credor é: </Typography>
                                            <Input
                                                id='credor'
                                                disabled={look}
                                                sx={{ width: '15vw' }}
                                                startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                                value={credor}
                                                onChange={(e) => setCredor(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={financeiro}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan2.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                    <div>
                                        <RadioGroup
                                            defaultValue=""
                                            name="select-service"
                                            value={financeiro}
                                            onChange={SelecionaFinanceiro}
                                        >
                                            {finan3.map((iten) => (
                                                <FormControlLabel key={iten.id} value={iten.id} control={<Radio size='small' />} label={iten.label} disabled={look} />
                                            ))}
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className={style.financ}>
                                    <Typography noWrap>Agente Financeiro: </Typography>
                                        <Input
                                            id="standard-adornment-amount"
                                            disabled={look}
                                            value={agente}
                                            sx={{ width: '65vw' }}
                                            startAdornment={<InputAdornment position="start"></InputAdornment>}
                                            onChange={(e) => setAgente(e.target.value)}
                                        />
                                </div>
                                <FormControlLabel
                                    defaultValue=""
                                    onChange={SelecionaEndereco}
                                    value='sim'
                                    label='Utilizar o endereço da CNH emitida no RS'
                                    control={<Checkbox size="small" />}
                                />
                                <Typography>Houve alteração de endereço após o último licenciamento do veículo?</Typography>
                                <RadioGroup
                                    defaultValue=""
                                    name="select-service"
                                    value={confirmaendereco}
                                    onChange={AlteraEndereco}
                                >
                                    <FormControlLabel value='nao' control={<Radio size="small" />} label='Não' />
                                    <FormControlLabel value='sim' control={<Radio size="small" />} label='Sim. Novo endereço, conforme comprovante de residência apresentado neste serviço:' />
                                </RadioGroup>
                                <TextField variant="standard" size="small" disabled={look} fullWidth></TextField>
                                <div className={style.container1}>
                                    <div className={style.outro1}>
                                        <Typography noWrap >Telefone celular do Proprietário, adquirente, curador ou inventariante:</Typography>
                                        <TextField sx={{ width: '48%' }} onChange={(e) => setTelefone(e.target.value)} variant="standard" size="small"></TextField>
                                    </div>
                                    <div className={style.outro1}>
                                        <Typography noWrap>Email do Proprietário, adquirente, curador ou inventariante:</Typography>
                                        <TextField sx={{ width: '55%' }} onChange={(e) => setEmail(e.target.value)} variant="standard" size="small"></TextField>
                                    </div>
                                    <FormControlLabel
                                        defaultValue=""
                                        onChange={AutorizaEndereco}
                                        value='sim'
                                        label='Autorizo o DETRAN/RS a enviar por email ou telefone celular informações de meu interesse junto a este órgão.'
                                        control={<Checkbox size="small" />}
                                    />
                                </div>
                                <div className={style.container2}>
                                    <Typography align="center" variant="h6">DECLARO, sob as penas da lei e sem prejuízo de demais sanções administrativas/cíveis/criminais, que</Typography>
                                    <Typography align="center" variant="h6">os documentos entregues ao DETRAN/RS para o serviço requerido são todos autênticos e, quando cópias,</Typography>
                                    <Typography align="center" variant="h6">condizem com o original e, caso haja produção de placa(s) de identificação veicular, que sou responsável</Typography>
                                    <Typography align="center" variant="h6">por assegurar a correta fixação da(s) placa(s) no veículo para o qual a estampagem foi autorizada, bem </Typography>
                                    <Typography align="center" variant="h6">como pela entrega das placas retiradas/substituídas na EPIV</Typography>
                                </div>
                                <div className={style.containerAssina}>
                                    <div className={style.assinatura1}>
                                        <Typography>Município:</Typography>
                                        <Input
                                            sx={{ width: '48%' }}
                                            id="standard-adornment-amount"
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography>, </Typography>
                                        <Input
                                            sx={{ width: '7%' }}
                                            id="standard-adornment-amount"
                                            value={data.dia}
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography> de </Typography>
                                        <Input
                                            sx={{ width: '20%' }}
                                            id="standard-adornment-amount"
                                            value={data.mes}
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography noWrap> de 20 </Typography>
                                        <Input
                                            sx={{ width: '7%' }}
                                            id="standard-adornment-amount"
                                            value={data.ano}
                                            startAdornment={[<InputAdornment position="start"></InputAdornment>, <InputAdornment position="start"></InputAdornment>]}
                                        />
                                        <Typography>.</Typography>
                                    </div>
                                    <div className={style.assinatura2}>
                                        <div className={style.linha}></div>
                                        <Typography>Assinatura do Responsável</Typography>
                                    </div>
                                </div>
                            </Box>
                            <Box>
                                <Button type='submit' >Enviar</Button>
                            </Box>
                        </form>
                    </Paper>
                </Box>
            </div>
        </>
    )
}
