import React from 'react';
import { useState } from 'react';
import { Cabecalho } from '../../api/cabecalho/Cabecalho'
import style from './Dashboard.module.scss'
import { TelaInicial } from './Inical/Inicial';
import { List, ListSubheader, ListItemButton, ListItemIcon, ListItemText, Collapse } from "@mui/material";
import HomeIcon from '@mui/icons-material/Home';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import GroupIcon from '@mui/icons-material/Group';
import PostAddIcon from '@mui/icons-material/PostAdd';
import KeyboardReturnIcon from '@mui/icons-material/KeyboardReturn';
import AssignmentIcon from '@mui/icons-material/Assignment';
import CommuteIcon from '@mui/icons-material/Commute';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import { CadastroUsuario } from './Usuario/Cadastro/CadastroUsuario';
import { ListarUsuario } from './Usuario/Listar/ListarUsuario';
import { EditarUsuario } from './Usuario/Editar/EditarUsuario';
import { Service } from './Servico/Servico';



const Dashboard = () => {

    const loginUser = JSON.parse(localStorage.getItem('usuario'))

    console.log(loginUser)

    const largura = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;

    const outdoor = [
        "Tela Inicial",
        "Cadastro de Usuário",
        "Lista de Usuário",
        "Editar Usuário",
        "Solicitar Serviço"
    ]

    const [tela, setTela] = useState({
        letreiro: outdoor[0],
        inicial: true,
        useradd: false,
        userlist: false,
        userEdit: false,
        service: false,
    })
    const [id, setId] = useState('')
    const [open, setOpen] = useState({
        bcadastro: false,
        bservico: false
    })


    const openUser = () => { setOpen(a => ({ ...a, bcadastro: !open.bcadastro, bsevico: false })) }
    const openService = () => { setOpen(a => ({ ...a, bcadastro: false, bservico: !open.bservico})) }
    const telaInicial = () => { setTela(a => ({ ...a, inicial: true, useradd: false, userlist: false, userView: false, userEdit: false, letreiro: outdoor[0] })) }
    const addUser = () => { setTela(a => ({ ...a, inicial: false, useradd: true, userlist: false, userView: false, userEdit: false, service: false, letreiro: outdoor[1] })) }
    const listUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: true, userView: false, userEdit: false, service: false, letreiro: outdoor[2] })) }
    const editUser = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: true, service: false, letreiro: outdoor[3] })) }
    const serVice = () => { setTela(a => ({ ...a, inicial: false, useradd: false, userlist: false, userView: false, userEdit: false, service: true, letreiro: outdoor[4] })) }


    return (
        <>
            <div className={style.cabeca}>
                <Cabecalho />
                <div className={style.containerFundo}>
                    <div className={style.info}>
                        <p>teste</p>
                        <p>{tela.letreiro}</p>
                        <p>teste</p>
                    </div>
                    <div className={style.container}>
                        <aside className={style.lateral}>
                            <List
                                sx={{ width: '100%', bgcolor: 'background.paper' }}
                                dense={true}
                                component="nav"
                                aria-labelledby="nested-list-subheader"
                                subheader={
                                    <ListSubheader component="div" id="nested-list-subheader">
                                        <p>Menu</p>
                                    </ListSubheader>
                                }
                            >
                                <ListItemButton onClick={telaInicial}>
                                    <ListItemIcon>
                                        <HomeIcon />
                                    </ListItemIcon>
                                    <ListItemText primary="Tela inicial" />
                                </ListItemButton>
                                {loginUser.usuariotipo !== 'preposto' ?
                                    <>
                                        <ListItemButton onClick={openUser}>
                                            <ListItemIcon>
                                                <AccountCircleIcon />
                                            </ListItemIcon>
                                            <ListItemText primary="Usuário" />
                                            {open.bcadastro ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        <Collapse in={open.bcadastro} timeout='auto' unmountOnExit>
                                            <List dense={true} component='div' >
                                                <ListItemButton sx={{ pl: 4 }} onClick={addUser}>
                                                    <ListItemIcon>
                                                        <PersonAddIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Cadastrar Usuário' />
                                                </ListItemButton>
                                                <ListItemButton sx={{ pl: 4 }} onClick={listUser}>
                                                    <ListItemIcon>
                                                        <GroupIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Listar Usuário' />
                                                </ListItemButton>
                                            </List>
                                        </Collapse>
                                    </>
                                : null}

                                {loginUser.usuariotipo !== 'master' ? loginUser.usuariotipo !== 'administrador' ?
                                    <>
                                        <ListItemButton onClick={openUser}>
                                            <ListItemIcon>
                                                <CommuteIcon />
                                            </ListItemIcon>
                                            <ListItemText primary="Serviço" />
                                            {open.bcadastro ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        <Collapse in={open.bcadastro} timeout='auto' unmountOnExit>
                                            <List dense={true} component='div' >
                                                <ListItemButton sx={{ pl: 4 }} onClick={serVice}>
                                                    <ListItemIcon>
                                                        <PostAddIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Solicitar Serviço' />
                                                </ListItemButton>
                                                <ListItemButton sx={{ pl: 4 }} onClick={listUser}>
                                                    <ListItemIcon>
                                                        <AssignmentIcon />
                                                    </ListItemIcon>
                                                    <ListItemText primary='Listar Serviço' />
                                                </ListItemButton>
                                            </List>
                                        </Collapse>
                                    </>
                                    : null : null}

                                <ListItemButton onClick={() => {
                                    localStorage.clear('usuario')
                                    window.location.href = "/"
                                }}>
                                    <ListItemIcon>
                                        <KeyboardReturnIcon />
                                    </ListItemIcon>
                                    <ListItemText primary="Sair" />
                                </ListItemButton>
                            </List>
                        </aside>
                        <div className={style.boxCentral}>
                            <div className={style.boxCentralContainer}>
                                {tela.inicial ? <TelaInicial user={loginUser.nome} /> : null}
                                {tela.useradd ? <CadastroUsuario user={loginUser} /> : null}
                                {tela.userlist ? <ListarUsuario user={loginUser} setId={setId} editUser={editUser} largura={largura} /> : null}
                                {tela.userEdit ? <EditarUsuario id={id} listUser={listUser} /> : null}
                                {tela.service ? <Service /> : null}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Dashboard;
