import { Box, Button, FormControl, InputLabel, MenuItem, Select, TextField } from "@mui/material"
import axios from "axios";
import { useState } from "react";
import style from './EditarUsuario.module.scss'



export const EditarUsuario = ({ id, listUser, loginUser }) => {

    const [resposta, setResposta] = useState()
    const [validaemail, setValidaemail] = useState(true)

    const [dados, setDados] = useState({
        _id: id._id,
        nome: id.nome,
        rg: id.rg,
        cpf: id.cpf,
        email: id.email,
        nCrddrs: id.nCrddrs,
        tel: id.tel,
        cep: id.cep,
        logradouro: id.logradouro,
        numero: id.numero,
        uf: id.uf,
        complemento: id.complemento,
        bairro: id.bairro,
        cidade: id.cidade,
        usuariotipo: id.usuariotipo,
        situacao: id.situacao
    })

    function userUpdate(e) {
        e.preventDefault();
        document.getElementById('aviso').innerHTML = ("");
        if (validaemail === false) {
            return document.getElementById('aviso').innerHTML = ("Email invalido favor tentar um email valido!");
        } else {
            document.getElementById("enviar").setAttribute("disabled", "disabled");
            setTimeout(() => {
                document.getElementById("enviar").removeAttribute("disabled");
            }, 5000);
            setTimeout(() => {
                axios.post('http://localhost:3001/Atualizar/Usuario', {
                    dados
                })
                    .then(res => {
                        // console.log(res)
                        if (res.data.matchedCount === 1) {
                            document.getElementById('aviso').innerHTML = ("Alteração no Cadastro Realizado com Sucesso!");
                            setResposta(style.sucesso);
                        } else {
                            document.getElementById('aviso').innerHTML = ("Usuário não Encontrado em Nosso Banco de Dados!");
                            setResposta(style.incorreto);
                        }
                    })
                    .catch(err => {
                        console.log(err);
                        document.getElementById('aviso').innerHTML = ("Erro ao Atualizar Cadastro, o Cadastro Não Foi Atualizado!");
                        setResposta(style.incorreto);
                    })
            }, 1000);
        }
    }

    const VerificaCEP = async (e) => {
        e.preventDefault();
        const a = e.target.value
        const cep = a.replace(/\D/g, '');
        if (cep !== '') {
            await fetch("https://viacep.com.br/ws/" + cep + "/json/")
                .then((response) => response.json())
                .then((responseJson) => {
                    setDados(a => ({
                        ...a,
                        logradouro: responseJson.logradouro,
                        uf: responseJson.uf,
                        complemento: responseJson.complemento,
                        bairro: responseJson.bairro,
                        cidade: responseJson.localidade
                    }))
                }
                )
        }
    };

    function validarEmail(e) {
        var dado = e.target.value
        if (dado === "") {
            setValidaemail(true)
        } else {
            setValidaemail(dado.includes("@"))
        }
    }

    return (
        <>
            <div id="aviso" className={resposta}></div>
            <div>
                <div id="aviso" className={resposta}></div>
                {dados &&
                    <form className={style.cadastroForm} onSubmit={userUpdate}>
                        <Box sx={{ minWidth: 120 }}>
                            {loginUser.usuariotipo === "despachante" ?
                                <FormControl variant="filled" sx={{ minWidth: 120 }} fullWidth>
                                    <InputLabel size="small" id="usuariosituacao" >Tipo de Usuário *</InputLabel>
                                    <Select
                                        labelId="usuariosituacao"
                                        id="selecionasituacao"
                                        value={dados.situacao}
                                        onChange={(e) => setDados(a => ({ ...a, situacao: e.target.value }))}
                                        label='Tipo de Usuário'
                                        variant="filled"
                                        size="small"
                                        required
                                    >
                                        <MenuItem value=''></MenuItem>
                                        <MenuItem value='ativo'>ATIVO</MenuItem>
                                        <MenuItem value='inativo'>INATIVO</MenuItem>
                                    </Select>
                                </FormControl>    
                        : null}
                        </Box>
                        <TextField onChange={(e) => setDados(a => ({ ...a, nome: e.target.value }))} value={dados.nome} className={style.cadastroItem} label='Nome' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, rg: e.target.value.replace(/\D/gm, "") }))} value={dados.rg} className={style.cadastroItem} label='RG' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, cpf: e.target.value.replace(/\D/gm, "") }))} value={dados.cpf} className={style.cadastroItem} label='CPF' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, email: e.target.value }))} value={dados.email} className={style.cadastroItem} helperText={!validaemail ? "Email inválido" : ""} error={!validaemail} onBlur={validarEmail} label='Email' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, nCrddrs: e.target.value }))} value={dados.nCrddrs} className={style.cadastroItem} label='Registro CRDD/RS' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, tel: e.target.value.replace(/\D/gm, "") }))} value={dados.tel} className={style.cadastroItem} label='Telefone' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, cep: e.target.value.replace(/\D/gm, "") }))} value={dados.cep} className={style.cadastroItem} onBlur={VerificaCEP} label='CEP' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, logradouro: e.target.value }))} value={dados.logradouro} className={style.cadastroItem} label='Rua' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, numero: e.target.value }))} value={dados.numero} className={style.cadastroItem} label='Numero' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, complemento: e.target.value }))} value={dados.complemento} className={style.cadastroItem} label='Complemento' variant="filled" size="small" />
                        <TextField onChange={(e) => setDados(a => ({ ...a, bairro: e.target.value }))} value={dados.bairro} className={style.cadastroItem} label='Bairro' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, cidade: e.target.value }))} value={dados.cidade} className={style.cadastroItem} label='Municipio' variant="filled" size="small" required />
                        <TextField onChange={(e) => setDados(a => ({ ...a, uf: e.target.value }))} value={dados.uf} className={style.cadastroItem} label='UF' variant="filled" size="small" required />
                        <Button type='submit' color='success' id='enviar'>Salvar alterações</Button>
                        <Button color="error" id='voltar' onClick={listUser}>Voltar a lista</Button>
                    </form>
                }
            </div>
        </>
    )
}
