import { Button, TextField } from '@mui/material';
import React from 'react';
import '../../sass/Index/index.css';
import crddrs from '../../img/crddrs.png'

const axios = require('axios').default;

const Inicial = () => {

    const d = new Date();
    const dt = Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)

    function loginRealizado(a) {
        if (a === "try") {
            document.getElementById('login--modal').showModal();
            document.getElementById('msg').innerHTML = "Verificando usuário e senha!"
        } else if (a === "successo") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = `Login realizado com sucesso! ${dt}`
            }, 3000)
        } else if (a === "incorreto") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Usuário ou senha incorreto."
            }, 3000)
        } else if (a === "erro") {
            setTimeout(() => {
                document.getElementById('msg').innerHTML = "Houve um erro ao tentar logar no servidor, tente novamente em alguns minutos."
            }, 3000)
        }
    }
    function fechaDiag() {
        let b = document.getElementById('msg').innerHTML
        if (b !== "Verificando usuário e senha!") {
            document.getElementById('login--modal').close();
        }
    }

    const LoginRealiza = () => {
        return (
            <>
                <dialog id='login--modal' onClick={fechaDiag}>
                    <div>
                        <p id='msg'></p>
                    </div>
                </dialog>
            </>
        )
    }

    const [login, setLogin] = React.useState();
    const [senha, setSenha] = React.useState();

    const Login = (e) => {
        e.preventDefault()
        loginRealizado("try")
        axios.get('http://localhost:3005/Logar', {
            params: {
                login: login,
                senha: senha
            }
        })
            .then((response) => {
                if (response.status === 200) {
                    if (!response.data) {
                        loginRealizado("incorreto")
                    } else {
                        console.log(response.data);
                        loginRealizado("successo")
                        setTimeout(() => {
                            console.log('teste')
                            window.location.href = "Dashboard";
                        }, 5000)
                        // axios.patch('http//localhost:3005/DataLogin', {
                        //     params: {
                        //         login: login,
                        //         senha: senha,
                        //         dataUltimoLogin: dt
                        //     }
                        // })
                    }
                }
            })
            .catch((err) => {
                console.log(err);
                loginRealizado("erro")
            })
            .finally(() => {
                document.getElementById('login--form').reset()
            })

    }

    return (
        <>
            <div className='title--bar'>
                <img className='crddrs' alt='crddrs' src={crddrs} />
                <h1>Conselho Regional dos Despachantes Documentalistas do Rio Grande do Sul</h1>
            </div>
            <div className='nav--bar'>
                <nav className='nav--item'>
                    <p>item 1</p>
                    <p>item 2</p>
                </nav>
            </div>
            <div className='box--central'>
                <div className='box--container'>
                    <h1>Confira os serviços disponíveis:</h1>
                </div>
                <div className='login'>
                    <h1>Login</h1>
                    <form id='login--form' onSubmit={Login}>
                        <div className='login-user'>
                            <TextField
                                id='login'
                                label='Usuário'
                                onChange={(e) => setLogin(e.target.value)}
                                color='success'
                                size='small'
                                fullWidth required />
                        </div>
                        <div className='login-user'>
                            <TextField
                                id='senha'
                                label='Senha'
                                type='password'
                                onChange={(e) => setSenha(e.target.value)}
                                color='success'

                                size='small'
                                fullWidth required />
                        </div>
                        <div className='login-user botao'>
                            <Button
                                variant='contained'
                                type='onSubmit'
                                color='success'
                                fullWidth>
                                Logar
                            </Button>
                        </div>
                        <p>Esqueci minha senha</p>
                    </form>
                    <LoginRealiza />
                </div>
            </div>
        </>
    )

}
export default Inicial
