import React from 'react';
import '../../sass/main.css'
import  Tela1  from '../tela1/Tela1';

const Inicial = () => {
    
    
    const [tela1, setTela1] = React.useState(false)
    const [tela2, setTela2] = React.useState(false)
    const [tela3, setTela3] = React.useState(false)
    const [tela4, setTela4] = React.useState(false)
    const [tela5, setTela5] = React.useState(false)
    const [tela6, setTela6] = React.useState(false)
    const [tela7, setTela7] = React.useState(false)
    const [tela8, setTela8] = React.useState(false)
    const [tela9, setTela9] = React.useState(false)
    const [tela10, setTela10] = React.useState(false)
    const [tela11, setTela11] = React.useState(false)
    const [tela12, setTela12] = React.useState(false)


    const botaoTela1 = () => {
        setTela1(!tela1)
        tela2 ? setTela2(tela2):setTela2(!tela2)
        tela3 ? setTela3(tela3):setTela3(!tela3)
        tela4 ? setTela4(tela4):setTela4(!tela4)
        tela5 ? setTela5(tela5):setTela5(!tela5)
        tela6 ? setTela6(tela6):setTela6(!tela6)
        tela7 ? setTela7(tela7):setTela7(!tela7)
        tela8
        tela9
        tela10
        tela11
        tela12
    }

    

    return (
        <>
            <nav className='menu--superior'>
                <div>
                    <p>teste1</p>
                    <p>teste2</p>
                    <p>teste3</p>
                </div>
            </nav>
            <aside className='menu--lateral'>
                <div>
                    {/* <p>item1</p> */}
                    <p className='item1' onClick={botaoTela1}>Alteração</p>
                    <p className='item2' onClick={botaoTela2}>Autorização</p>
                    <p className='item2' onClick={botaoTela3}>item3</p>
                    <p className='item2' onClick={botaoTela4}>item4</p>
                    <p className='item2' onClick={botaoTela5}>item5</p>
                    <p className='item2' onClick={botaoTela6}>item6</p>
                    <p className='item2' onClick={botaoTela7}>item7</p>
                    <p className='item2' onClick={botaoTela8}>item8</p>
                    <p className='item2' onClick={botaoTela9}>item9</p>
                    <p className='item2' onClick={botaoTela10}>item10</p>
                    <p className='item2' onClick={botaoTela11}>item11</p>
                    <p className='item2' onClick={botaoTela12}>item12</p>
                </div>
            </aside>
            <div className='tela--inicial'>
                <div>
                    <h1 id='CRDDRS'>CRDDRS</h1>
                    {!tela1 ? null : <Tela1 />}
                </div>
            </div>
        </>
    )
}

export default Inicial
