import { useState } from "react";
import axios from 'axios';
import style from './CadastroUsuario.module.scss'
import { Box, Button, FormControl, InputLabel, MenuItem, Select, TextField } from "@mui/material";

export const CadastroUsuario = ({ user, loginUser }) => {

    const [dados, setDados] = useState({
        nome: "",
        sobrenome: "",
        rg: "",
        cpf: "",
        email: "",
        nCrddrs: "",
        tel: "",
        cep: "",
        logradouro: "",
        numero: "",
        uf: "",
        complemento: "",
        bairro: "",
        cidade: "",
        usuariotipo: "",
        login: "",
        senha: "",
        pai: user.id
    })
    const [validaemail, setValidaemail] = useState(true);
    const [validalogin, setValidalogin] = useState(true);
    const [resposta, setResposta] = useState("");

    const ValidaLogin = (e) => {
        e.preventDefault()
        axios.get('http://localhost:3001/ValidaUsuario', {
            params: {
                login: e.target.value
            }
        })
            .then((response) => {
                if (response.status === 200) {
                    if (response.data === 'valido') {
                        setValidalogin(true)
                    } else {
                        setValidalogin(false)
                    }
                } else {
                    console.log("erro")
                }
            })
            .catch((err) => console.log(err))
    }

    const validarEmail = (e) => {
        dados.email === '' ? setValidaemail(true) : setValidaemail(dados.email.includes("@"));
    }

    const VerificaCEP = async (e) => {
        if (dados.cep !== '') {
            await fetch("https://viacep.com.br/ws/" + dados.cep + "/json/")
                .then((response) => response.json())
                .then((responseJson) => {
                    // console.log(responseJson)
                    setDados(a => ({
                        ...a,
                        logradouro: responseJson.logradouro,
                        uf: responseJson.uf,
                        complemento: responseJson.complemento,
                        bairro: responseJson.bairro,
                        cidade: responseJson.localidade
                    }))
                }
                )
        }
    };

    function cadastrarUser(e) {
        e.preventDefault()
        if (validaemail === false) {
            return document.getElementById('aviso').innerHTML = ("Email invalido favor tentar um email valido!")
        } else if (validalogin === false) {
            return document.getElementById('aviso').innerHTML = ("Login já utilizado, favor tentar outro.")

        } else {
            document.getElementById("enviar").setAttribute("disabled", "disabled")
            setTimeout(() => {
                document.getElementById("enviar").removeAttribute("disabled")
            }, 3000)
            axios.post('http://localhost:3001/Cadastro/Usuario', {
                dados
            })
                .then(res => {
                    document.getElementById('aviso').innerHTML = ("Cadastro Realizado com Sucesso!");
                    setResposta(style.sucesso);
                })
                .catch(err => {
                    document.getElementById('aviso').innerHTML = ("Erro ao Cadastrar, o Cadastro Não Foi Realizado!");
                    setResposta(style.incorreto);
                })
        }
    }

    return (
        <>
            <div id="aviso" className={resposta}></div>
            {dados &&
                <form className={style.cadastroForm} onSubmit={cadastrarUser}>
                    {loginUser.usuariotipo === 'master' ?
                        <Box sx={{ minWidth: 120 }}>
                            <FormControl variant="filled" sx={{ minWidth: 120 }} fullWidth>
                                <InputLabel size="small" id="usuariotipo" >Tipo de Usuário *</InputLabel>
                                <Select
                                    labelId="usuariotipo"
                                    id="selecionatipo"
                                    value={dados.usuariotipo}
                                    onChange={(e) => setDados(a => ({ ...a, usuariotipo: e.target.value }))}
                                    label='Tipo de Usuário'
                                    variant="filled"
                                    size="small"
                                    required
                                >
                                    <MenuItem value='master'>Master</MenuItem>
                                    <MenuItem value='administrador'>Administrador</MenuItem>
                                    <MenuItem value='despachante'>Despachante</MenuItem>
                                    <MenuItem value='preposto'>Preposto</MenuItem>
                                </Select>
                            </FormControl>
                        </Box>
                    : null}
                    <TextField onChange={(e) => setDados(a => ({ ...a, nome: e.target.value }))} value={dados.nome} className={style.cadastroItem} label='Nome' variant="filled" size="small" title="Preencha o nome completo" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, rg: e.target.value.replace(/\D/gm, "") }))} value={dados.rg} className={style.cadastroItem} label='RG' variant="filled" size="small" title="Digite o numero do RG" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, cpf: e.target.value.replace(/\D/gm, "") }))} value={dados.cpf} className={style.cadastroItem} label='CPF' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, email: e.target.value }))} helperText={!validaemail ? "Email inválido" : ""} error={!validaemail} onBlur={validarEmail} value={dados.email} className={style.cadastroItem} label='Email' variant="filled" size="small" required />
                    {dados.usuariotipo === 'master' ? null : dados.usuariotipo === "administrador" ? null : <TextField onChange={(e) => setDados(a => ({ ...a, nCrddrs: e.target.value }))} value={dados.nCrddrs} className={style.cadastroItem} label='Registro CRDD/RS' variant="filled" size="small" required />}
                    <TextField onChange={(e) => setDados(a => ({ ...a, login: e.target.value }))} onBlur={ValidaLogin} helperText={!validalogin ? "Login ja utilizado" : ""} error={!validalogin} value={dados.login} className={style.cadastroItem} label='Login' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, senha: e.target.value }))} value={dados.senha} className={style.cadastroItem} type='password' label='Senha' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, tel: e.target.value.replace(/\D/gm, "") }))} value={dados.tel} className={style.cadastroItem} label='Telefone' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, cep: e.target.value.replace(/\D/gm, "") }))} onBlur={VerificaCEP} value={dados.cep} className={style.cadastroItem} label='CEP' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, logradouro: e.target.value }))} value={dados.logradouro} className={style.cadastroItem} label='Rua' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, numero: e.target.value }))} value={dados.numero} className={style.cadastroItem} label='Numero' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, complemento: e.target.value }))} value={dados.complemento} className={style.cadastroItem} label='Complemento' variant="filled" size="small" />
                    <TextField onChange={(e) => setDados(a => ({ ...a, bairro: e.target.value }))} value={dados.bairro} className={style.cadastroItem} label='Bairro' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, cidade: e.target.value }))} value={dados.cidade} className={style.cadastroItem} label='Municipio' variant="filled" size="small" required />
                    <TextField onChange={(e) => setDados(a => ({ ...a, uf: e.target.value }))} value={dados.uf} className={style.cadastroItem} label='UF' variant="filled" size="small" required />
                    <Button id='enviar' color="success" variant="outlined" type="submit">Cadastrar</Button>
                </form>}
        </>
    )
}
