import { TextField } from "@mui/material"
import React from "react"
import style from './Identificador.module.scss'
import InputMask from "react-input-mask";

export const Identificador = ({ placa, setPlaca }) => {

    var passo1 = false
    
    if (placa.length > 7) {
        passo1 = true
    }

    const Placa = (e) => {
        setPlaca(e.target.value.toUpperCase().slice(0,7))
    }


    return (
        <>
            <div className={style.formulario}>
                <InputMask
                    mask="aaa999"
                    value={placa}
                    disabled={false}
                    maskChar=" "
                >{() => 
                    <TextField
                        id="placa"
                        sx={{ minWidth: '30vw', margin: 2 }}
                        label='Placa'
                        placeholder='Digite a placa do veículo'
                        error={passo1}
                        helperText={passo1 ? "Placa inválida" : null}
                        value={placa}
                        onChange={Placa}
                        size='small'
                        required
                    />}

                </InputMask>
            </div>
        </>
    )
}
