import React from 'react';
import { useEffect } from 'react';
import { ApesquisaRealizada, Modal } from '../../api/alerta/alertas';
import { FormularioPesquisa } from '../../api/alerta/formulario/FormPesq';

const AIProp = () => {

    const [dado, setDado] = React.useState('');
    const [opt, setOpt] = React.useState('placa');

    const EnvioDados = (e) => {
        e.preventDefault()
        console.log('enviado', dado, opt)
        if(dado === ''){
            alert("O campo de "+ opt + " não pode ficar vazio!")
        } else {
            Modal(dado,opt)
            document.getElementById('formulario').reset()
            setDado('')
        }
    }

    useEffect(() => {
        if(opt === 'placa'){
            document.getElementById('btn').placeholder='Digite a placa aqui!'
        } else if(opt === 'chassi'){
            document.getElementById('btn').placeholder='Digite o chassi aqui!'
        } 
    },[opt])

    console.log(dado, 'teste', opt)

    return (<FormularioPesquisa pesquisa='Alteração de Informações do Proprietário' IdPesquisa='0'/>)            
}
export default AIProp
