import * as React from 'react';
import PropTypes from 'prop-types';
import { alpha } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import TableSortLabel from '@mui/material/TableSortLabel';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import DeleteIcon from '@mui/icons-material/Delete';
import SearchIcon from '@mui/icons-material/Search';
import { visuallyHidden } from '@mui/utils';
import axios from 'axios';
import { Button } from '@mui/material';
import style from './ListarUsuario.module.scss'
import EditIcon from '@mui/icons-material/Edit';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import Collapse from '@mui/material/Collapse';


function descendingComparator(a, b, orderBy) {
    if (b[orderBy] < a[orderBy]) {
        return -1;
    }
    if (b[orderBy] > a[orderBy]) {
        return 1;
    }
    return 0;
}

function getComparator(order, orderBy) {
    return order === 'desc'
        ? (a, b) => descendingComparator(a, b, orderBy)
        : (a, b) => -descendingComparator(a, b, orderBy);
}

// This method is created for cross-browser compatibility, if you don't
// need to support IE11, you can use Array.prototype.sort() directly
function stableSort(array, comparator) {
    const stabilizedThis = array.map((el, index) => [el, index]);
    stabilizedThis.sort((a, b) => {
        const order = comparator(a[0], b[0]);
        if (order !== 0) {
            return order;
        }
        return a[1] - b[1];
    });
    return stabilizedThis.map((el) => el[0]);
}

const headCells = [
    {
        id: 'nome',
        numeric: false,
        disablePadding: true,
        label: 'Nome',
    },
    {
        id: 'rg',
        numeric: true,
        disablePadding: false,
        label: 'RG',
    },
    {
        id: 'cfp',
        numeric: true,
        disablePadding: false,
        label: 'CPF',
    },
    {
        id: 'tel',
        numeric: true,
        disablePadding: false,
        label: 'Telefone',
    },
    {
        id: 'email',
        numeric: true,
        disablePadding: false,
        label: 'Email',
    },
];

function EnhancedTableHead(props) {
    const { order, orderBy, onRequestSort } = props;
    const createSortHandler = (property) => (event) => {
        onRequestSort(event, property);
    };

    return (
        <TableHead>
            <TableRow>
                <TableCell />
                {headCells.map((headCell) => (
                    <TableCell
                        key={headCell.id}
                        align={headCell.numeric ? 'right' : 'left'}
                        padding={headCell.disablePadding ? 'none' : 'normal'}
                        sortDirection={orderBy === headCell.id ? order : false}
                    >
                        <TableSortLabel
                            active={orderBy === headCell.id}
                            direction={orderBy === headCell.id ? order : 'asc'}
                            onClick={createSortHandler(headCell.id)}
                        >
                            {headCell.label}
                            {orderBy === headCell.id ? (
                                <Box component="span" sx={visuallyHidden}>
                                    {order === 'desc' ? 'sorted descending' : 'sorted ascending'}
                                </Box>
                            ) : null}
                        </TableSortLabel>
                    </TableCell>
                ))}
            </TableRow>
        </TableHead>
    );
}

EnhancedTableHead.propTypes = {
    onRequestSort: PropTypes.func.isRequired,
    order: PropTypes.oneOf(['asc', 'desc']).isRequired,
    orderBy: PropTypes.string.isRequired,
};

function EnhancedTableToolbar(props) {

    const [search, setSearch] = React.useState(false)

    return (
        <Toolbar
            sx={{
                pl: { sm: 2 },
                pr: { xs: 1, sm: 1 },
            }}
        >
            <Tooltip title="Buscar">
                <IconButton>
                    <SearchIcon />
                </IconButton>
            </Tooltip>
        </Toolbar>
    );
}


const Linha = ({ setItens, setId, editUser, labelId, row, dense }) => {

    const [open, setOpen] = React.useState(false)


    const DelUser = (e) => {
        setItens(e)
        document.getElementById('avisaDel').showModal();
        document.getElementById('confirmaDel').innerHTML = (`Confirme para deletar ${e.nome}`)
    }
    function Editar(id) {
        setId(id);
        editUser();
    }

    return (
        <>
            <TableRow
                hover
            >
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                    <div className={style.botoes}>
                        <button title="Editar" onClick={() => Editar(row)}>
                            <EditIcon fontSize="small" color='success' />
                        </button>
                        <button title="Apagar">
                            <DeleteIcon onClick={() => DelUser(row)} fontSize="small" color="error" />
                        </button>
                    </div>
                </TableCell>
                <TableCell
                    component="th"
                    id={labelId}
                    scope="row"
                    padding="none"
                >
                    {row.nome}
                </TableCell>
                <TableCell align="right">{row.rg}</TableCell>
                <TableCell align="right">{row.cpf}</TableCell>
                <TableCell align="right">{row.tel}</TableCell>
                <TableCell align="right">{row.email}</TableCell>
                <TableCell>
                    <IconButton
                        aria-label="expand row"
                        size="small"
                        onClick={() => setOpen(!open)}
                    >
                        {open ? <KeyboardArrowUpIcon color='primary' /> : <KeyboardArrowDownIcon color='primary' />}
                    </IconButton>
                </TableCell>
            </TableRow>
            <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} >
                <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6} >
                    <Collapse in={open} timeout="auto" unmountOnExit>
                        <Box sx={{ margin: 1 }}>
                            <Typography color='primary' variant="h6" gutterBottom component="div">
                                Detalhes <p></p>
                                <Button sx={{ left: '82%' }} color='success' onClick={() => Editar(row)}>Editar</Button>
                                <Button sx={{ left: '82%' }} color='error' onClick={() => DelUser(row)}>Apagar</Button>
                            </Typography>
                            <Table aria-label="purchases" >
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Cep</TableCell>
                                        <TableCell>Rua</TableCell>
                                        <TableCell align="right">Numero</TableCell>
                                        <TableCell align="right">Complemento</TableCell>
                                        <TableCell align="right">Bairro</TableCell>
                                        <TableCell align="right">Cidade</TableCell>
                                        <TableCell align="right">UF</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody >
                                    <TableRow >
                                        <TableCell component="th" scope="row">{row.cep}</TableCell>
                                        <TableCell>{row.logradouro}</TableCell>
                                        <TableCell align="center">{row.numero}</TableCell>
                                        <TableCell align="right">{row.complemento}</TableCell>
                                        <TableCell align="right">{row.bairro}</TableCell>
                                        <TableCell align="right">{row.cidade}</TableCell>
                                        <TableCell align="right">{row.uf}</TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                        </Box>
                    </Collapse>
                </TableCell>
            </TableRow>
        </>
    )
}

export function ListarUsuario({ editUser, setId, viewUser }) {
    const [rows, setRows] = React.useState()
    const [order, setOrder] = React.useState('asc');
    const [orderBy, setOrderBy] = React.useState('_id');
    const [page, setPage] = React.useState(0);
    const [dense, setDense] = React.useState(false);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    React.useEffect(() => {
        axios.get('http://localhost:3001/Usuarios/Listar')
            .then(res => {
                setRows(res.data)
            })
            .catch(err => console.log(err))
    }, []);

    const handleRequestSort = (event, property) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
    };



    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handleChangeDense = (event) => {
        setDense(event.target.checked);
    };


    // Avoid a layout jump when reaching the last page with empty rows.
    const emptyRows =
        page > 0 ? Math.max(0, (1 + page) * rowsPerPage - rows.length) : 0;

    function DeleteUser() {
        let { _id } = itens
        axios.post('http://localhost:3001/Usuario/Deletar', { _id })
            .then(res => {
                if (res.data.deletedCount === 0) { return; }
                else {
                    axios.get('http://localhost:3001/Usuarios/Listar')
                        .then(res => setRows(res.data))
                        .catch(err => console.log(err))
                }
            })
            .catch(err => console.log(err))
    }
    const [itens, setItens] = React.useState("")



    if (rows) {
        return (
            <Box sx={{ width: '100%' }}>

                <dialog id='avisaDel' className={style.DialogoDeletar} onClick={() => document.getElementById("avisaDel").close()}>
                    <div>
                        <p id='confirmaDel'>teste</p>
                        <Button onClick={DeleteUser} variant='outlined' >Confirmar</Button>
                        <Button onClick={() => document.getElementById("avisaDel").close()} variant='outlined' >Cancelar</Button>
                    </div>
                </dialog>
                <Paper sx={{ width: '100%', mb: 2 }}>
                    <EnhancedTableToolbar />
                    <TableContainer>
                        <Table
                            sx={{ minWidth: 750 }}
                            aria-labelledby="tableTitle"
                            size={dense ? 'small' : 'medium'}
                        >
                            <EnhancedTableHead
                                order={order}
                                orderBy={orderBy}
                                onRequestSort={handleRequestSort}
                            />
                            <TableBody>
                                {stableSort(rows, getComparator(order, orderBy))
                                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    .map((row, index) => {
                                        const labelId = `enhanced-table-checkbox-${index}`;

                                        return (
                                            <React.Fragment>
                                                <Linha
                                                    key={row._id}
                                                    setItens={setItens}
                                                    setId={setId}
                                                    editUser={editUser}
                                                    labelId={labelId}
                                                    row={row}
                                                    dense={dense}
                                                />
                                            </React.Fragment>
                                        );
                                    })}
                                {emptyRows > 0 && (
                                    <TableRow
                                        style={{
                                            height: (dense ? 33 : 53) * emptyRows,
                                        }}
                                    >
                                        <TableCell colSpan={6} />
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[10, 25, 50, 100, 1000]}
                        component="div"
                        count={rows.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>
                <FormControlLabel
                    control={<Switch checked={dense} onChange={handleChangeDense} />}
                    label="Modo compacto"
                />
            </Box>
        );
    } else {
        return <p>Loading...</p>
    }
}
