import { useEffect, useState } from "react";
import style from './ListarUsuario.module.scss';
import DeleteIcon from '@mui/icons-material/Delete';
import SearchIcon from '@mui/icons-material/Search';
import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';
import { Button, Divider, IconButton, InputBase, Paper } from "@mui/material";

export function ListarUsuario({viewUser, setId, editUser}) {


    const [usuario, setUsuario] = useState("")
    const [itens, setItens] = useState("")
    const [ verificador, setVerificador ] = useState({
        vnome: true,
        vsobrenome: true,
        vrg: true,
        vcpf: true,
        vemail: true,
        vtelefone: true,
        vcrddrs: true,
        vcep: true,
        vid: true,
    })

    useEffect(() => {
        axios.get('http://localhost:3001/Usuarios/Listar')
        .then(res => {
            setUsuario(res.data)
        })
        .catch(err => console.log(err))
    }, []);
    
    const DelUser = (e) => {
        console.log(e)
        setItens(e)
        document.getElementById('avisaDel').showModal();
        document.getElementById('confirmaDel').innerHTML = (`Confirme para deletar ${e.nome}`)
    }

    function DeleteUser() {
        let { _id } = itens
        axios.post('http://localhost:3001/Usuario/Deletar', { _id })
        .then(res => {
            if (res.data.deletedCount === 0) { return; }
            else {
                axios.get('http://localhost:3001/Usuarios/Listar')
                .then(res => setUsuario(res.data))
                .catch(err => console.log(err))
        }})
        .catch(err => console.log(err))
    }

    const buscaUser = (e) => {
        e.preventDefault()
        const {value} = e.target[0]
        console.log(value)
        if (value === '') {
            axios.get('http://localhost:3001/Usuarios/Listar')
            .then(res => { setUsuario(res.data) })
            .catch(err => console.log(err))
        } else{
            console.log("foi")
            axios.get(`http://localhost:3001/Usuario/Listar/User=${value}`)
            .then((response) => {
                if(response.data.length === 0){
                    axios.get('http://localhost:3001/Usuarios/Listar')
                    .then(res => { 
                        console.log(res.data)
                        setUsuario(res.data) })
                    .catch(err => console.log(err))
                } else {
                    setUsuario(response.data)
                }
            })
            .catch((err) => console.log(err))
        }
    }
    function ordenaNome() {
        if(verificador.vnome === true){ setVerificador(e => ({...e, vnome: false})); setUsuario(usuario.sort(compareNomeCrescente)); }
        else { setVerificador(e => ({...e, vnome: true})); setUsuario(usuario.sort(compareNomeDecrescente)); }
    }
    function compareNomeCrescente(a,b) { return a.nome === b.nome ? 0 : a.nome > b.nome ? 1 : -1; }
    function compareNomeDecrescente(a,b) { return a.nome === b.nome ? 0 : a.nome < b.nome ? 1 : -1; }

    function ordenaRg() {
            if(verificador.vrg === true){ setVerificador(e => ({...e, vrg: false})); setUsuario(usuario.sort(compareRgCrescente)); } 
            else { setVerificador(e => ({...e, vrg: true})); setUsuario(usuario.sort(compareRgDecrescente)); }
    }
    function compareRgCrescente(a,b) { return a.rg === b.rg ? 0 : a.rg > b.rg ? 1 : -1; }
    function compareRgDecrescente(a,b) { return a.rg === b.rg ? 0 : a.rg < b.rg ? 1 : -1; }

    function ordenaCpf() {
        if(verificador.vcpf === true){ setVerificador(e => ({...e, vcpf: false})); setUsuario(usuario.sort(compareCpfCrescente)); }
        else { setVerificador(e => ({...e, vcpf: true})); setUsuario(usuario.sort(compareCpfDecrescente)); }
    }
    function compareCpfCrescente(a,b) { return a.cpf === b.cpf ? 0 : a.cpf > b.cpf ? 1 : -1; }
    function compareCpfDecrescente(a,b) { return a.cpf === b.cpf ? 0 : a.cpf < b.cpf ? 1 : -1; }

    function ordenaEmail() {
        if(verificador.vemail === true){ setVerificador(e => ({...e, vemail: false})); setUsuario(usuario.sort(compareEmailCrescente)); }
        else { setVerificador(e => ({...e, vemail: true})); setUsuario(usuario.sort(compareEmailDecrescente)); }
    }
    function compareEmailCrescente(a,b) { return a.email === b.email ? 0 : a.email > b.email ? 1 : -1; }
    function compareEmailDecrescente(a,b) { return a.email === b.email ? 0 : a.email < b.email ? 1 : -1; }

    function ordenaTel() {
        if(verificador.vtelefone === true){ setVerificador(e => ({...e, vtelefone: false})); setUsuario(usuario.sort(compareTelCrescente)); }
        else { setVerificador(e => ({...e, vtelefone: true})); setUsuario(usuario.sort(compareTelDecrescente)); }
    }
    function compareTelCrescente(a,b) { return a.tel === b.tel ? 0 : a.tel > b.tel ? 1 : -1; }
    function compareTelDecrescente(a,b) { return a.tel === b.tel ? 0 : a.tel < b.tel ? 1 : -1; }

    function ordenaRegistro() {
        if(verificador.vcrddrs === true){ setVerificador(e => ({...e, vcrddrs: false})); setUsuario(usuario.sort(compareNcrddrsCrescente)); }
        else { setVerificador(e => ({...e, vcrddrs: true})); setUsuario(usuario.sort(compareNcrddrsDecrescente)); }
    }
    function compareNcrddrsCrescente(a,b) { return a.nCrddrs === b.nCrddrs ? 0 : a.nCrddrs > b.nCrddrs ? 1 : -1; }
    function compareNcrddrsDecrescente(a,b) { return a.nCrddrs === b.nCrddrs ? 0 : a.nCrddrs < b.nCrddrs ? 1 : -1; }

    function ordenaCep() {
        if(verificador.vcep === true){ setVerificador(e => ({...e, vcep: false})); setUsuario(usuario.sort(compareCepCrescente)); }
        else { setVerificador(e => ({...e, vcep: true})); setUsuario(usuario.sort(compareCepDecrescente)); }
    }
    function compareCepCrescente(a,b) { return a.cep === b.cep ? 0 : a.cep > b.cep ? 1 : -1; }
    function compareCepDecrescente(a,b) { return a.cep === b.cep ? 0 : a.cep < b.cep ? 1 : -1; }


    function Funcao(id) {
        setId(id);
        viewUser();
    }

    const Lista = ({usuario}) => {
        return(
            <>
            { !usuario && <caption>Loading...</caption> }
            { usuario && usuario.map((itens) => {
                return (
                    <tbody key={itens._id}>
                        <tr>
                            {/* <td>{itens._id}</td> */}
                            <td title={"Nome: "+itens.nome} >{itens.nome}</td>
                            <td title={"RG: "+itens.rg}>{itens.rg}</td>
                            <td title={"CPF: "+itens.cpf}>{itens.cpf}</td>
                            <td title={"Email: "+itens.email}>{itens.email}</td>
                            <td title={"Telefone: "+itens.tel}>{itens.tel}</td>
                            <td title={"Registro CRDDRS: "+itens.nCrddrs}>{itens.nCrddrs}</td>
                            <td title={"CEP: "+itens.cep}>{itens.cep}</td>
                            {/* <td>{itens.rua}</td>
                            <td>{itens.numero}</td>
                            <td>{itens.complemento}</td>
                            <td>{itens.bairro}</td>
                            <td>{itens.cidade}</td>
                            <td>{itens.uf}</td> */}
                            <td>
                                <div>
                                    <button title="Visualizar" onClick={() => Funcao(itens)} >
                                        <SearchIcon fontSize="small" color="primary" />
                                    </button>
                                    <button title="Editar" onClick={editUser}>
                                        <EditIcon fontSize="small" color='warning' />
                                    </button>
                                    <button title="Apagar">
                                        <DeleteIcon onClick={() => DelUser(itens)} fontSize="small" color="error" />
                                    </button>
                                </div>
                            </td>
                        </tr>
                    </tbody>
                )
            })}
            </>
        )
    }
    
    

    return (
        <>
            <dialog id='avisaDel' className={style.DialogoDeletar} onClick={() => document.getElementById("avisaDel").close()}>
                <div>
                    <p id='confirmaDel'>teste</p>
                    <Button onClick={DeleteUser} variant='outlined' >Confirmar</Button>
                    <Button onClick={() => document.getElementById("avisaDel").close()} variant='outlined' >Cancelar</Button>
                </div>
            </dialog>
            <div className={style.busca}>
                <Paper
                    component="form"
                    onSubmit={buscaUser}
                    elevation={6}
                    sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: 400 }}
                >
                    <InputBase
                        id="search"
                        sx={{ ml: 1, flex: 1 }}
                        // onChange={buscaUser}
                        placeholder="Buscar usuário"
                        inputProps={{ 'aria-label': 'Buscar usuário' }}
                        autoFocus
                    />
                    <IconButton type="submit" sx={{ p: '10px' }} aria-label="search">
                        <SearchIcon />
                    </IconButton>
                    <Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />
                </Paper>
            </div>
            <table id='lista' className={style.listaCliente}>
                <thead>
                    <tr>
                        {/* <th>ID</th> */}
                        <th onClick={ordenaNome}>NOME</th>
                        <th onClick={ordenaRg}>RG</th>
                        <th onClick={ordenaCpf}>CPF</th>
                        <th onClick={ordenaEmail}>EMAIL</th>
                        <th onClick={ordenaTel}>TELEFONE</th>
                        <th onClick={ordenaRegistro}>REGISTRO</th>
                        <th onClick={ordenaCep}>CEP</th>
                        {/* <th>RUA</th>
                        <th>NUMERO</th>
                        <th>COMPLEMENTO</th>
                        <th>BAIRRO</th>
                        <th>CIDADE</th>
                        <th>UF</th> */}
                        <th></th>
                    </tr>
                </thead>
                <Lista usuario={usuario}/>
            </table>
        </>
    )
}
