    const [tela1, setTela1] = React.useState(false);
    const [tela2, setTela2] = React.useState(false);
    const [tela3, setTela3] = React.useState(false);

    botaoOpt1 = () => {
        !tela1 ? setTela1(tela1):setTela1(!tela1)
    }

    console.log('Tela1')
    return(
        <div className='tela--inicial--tela1'>
            <p className='item' onClick={botaoOpt1} >Alteração de Informações do Proprietário</p>
            <p className='item' onClick={botaoOpt2} >Alteração de Informações do Veículo</p>
            <p className='item' onClick={botaoOpt3} >Alteração de Restrição Financeira</p>
        </div>
    )
}

export default Tela1
