import React, { useState } from 'react';
import style from './Inicial.module.scss'
import { Cabecalho } from '../../api/cabecalho/Cabecalho';
import { Rodape } from '../../api/rodape/Rodape'
import { Login } from './Login/Login';
import { Sobre } from './Sobre/Sobre';


const Inicial = () => {
    
    const [ tela, setTela ] = useState({
        inicial: true,
        sobre: false,
    })

    function Tela(e) {
        e.target.id === 'inicial' && setTela(a => ({...a, inicial: true, sobre: false}))
        e.target.id === 'sobre' && setTela(a => ({...a, inicial: false, sobre: true }))
    }



    return (
        <>
            <Cabecalho />
            <div className={style['nav--bar']}>
                <nav className={style['nav--item']}>
                    <p id='inicial' onClick={Tela}>Inicial</p>
                    <p id='sobre' onClick={Tela}>Sobre</p>
                </nav>
            </div>
            <div className={style['box--central']}>
                {tela.inicial ? <Login />:null}
                {tela.sobre ? <Sobre />:null}
            </div>
            <Rodape />
        </>
    )

}
export default Inicial
