import { Button, IconButton, Paper, Typography } from "@mui/material"
import { Box } from "@mui/system"
import AddIcon from '@mui/icons-material/Add';
import HorizontalRuleIcon from '@mui/icons-material/HorizontalRule';
import { useState } from "react";

export const SolicitarGrt = ({ loginUser }) => {

    const [qtd, setQtd] = useState({
        valor: 1.50,
        quantidade: 1
    })

    const valorPadrão = 1.50
    var valida = false

    const Add = () => {
        setQtd(a => ({...a, valor: qtd.valor + 1.50}))
    }

    const Sub = () => {
        setQtd(a => ({...a, valor: qtd.valor - 1.50}))
    }
    
    if(qtd.valor === 1.50) {
        document.getElementById('minus').setAttribute('disabled', 'desabled')
    }

    return (
        <>
            <Typography variant='h6' color='primary'> Solicitar GRT</Typography>

            <Box sx={{ width: '100%' }}>
                <Paper sx={{ p: 4, m: 2 }} elevation={6} >
                    <Typography>Valor por GRT: R$ {valorPadrão.toFixed(2)} </Typography>
                    
                    <Typography>Valor total da GRT: R$ {qtd.valor.toFixed(2)} </Typography>

                    <IconButton onClick={Add}>
                        <AddIcon />
                    </IconButton>
                    <IconButton id='minus' onClick={Sub} disabled={valida} >
                        <HorizontalRuleIcon />
                    </IconButton>
                </Paper>
            </Box>
            <Button>Solicitar</Button>
        </>
    )
}
