import * as React from 'react';
import { Box, Button, InputLabel, MenuItem, Paper, Select } from "@mui/material"
import axios from "axios"
import { useState } from "react"
import style from './UploadArquivo.module.scss'

export const UploadArquivo = ({ user }) => {

    const [retorno, setRetorno] = React.useState()
    const [grt, setGrt] = React.useState()

    const escolheGrt = (e) => {
        setGrt(e.target.value)
    }

    React.useEffect(() => {
        axios.get("http://localhost:3001/GRT/Utilizaveis", {
            params: { user }
        })
            .then(res => setRetorno(res))
            .catch(err => console.log(err))
    }, [])

    const [teste, setTeste] = useState()

    const salvarGrt = () => {

    }

    return (
        <>
            <Paper sx={{ p: 2 }}>
                <Box sx={{ width: '100%' }}>
                    <form className={style.container} onSubmit={salvarGrt}>
                        <div className={style.botaoUp}>
                            <InputLabel id="selecionaGrt">Selecione a GRT</InputLabel>
                            <Select
                                labelId="selecionaGrt"
                                id="SelGRT"
                                value={grt}
                                label='GRT'
                                onChange={escolheGrt}
                            >
                                {retorno !== undefined ? retorno.map((iten) => {
                                    return (
                                        <MenuItem key={iten.id} valeu={iten}>{iten}</MenuItem>
                                    )
                                }) : null}
                            </Select>
                            <Button variant="contained" component="label">
                                Enviar Arquivos
                                <input hidden accept="image/*" multiple type="file" onChange={(e) => setTeste(e.target.files)} />
                            </Button>
                        </div>
                        <div className={style.boxConteiner}>
                            <div className={style.amostra}>
                                <p>Nenhum arquivo selecionado</p>
                                {teste ? teste : ''}
                            </div>
                        </div>

                        <div className={style.botao}>
                            <Button color='success' type="submit">Salvar</Button>
                        </div>

                    </form>
                </Box>
            </Paper>
        </>
    )
}
