import { Button, TextField } from '@mui/material';
import React from 'react';
import '../../sass/Index/index.css';
import { toast, ToastContainer } from 'react-toastify'

const axios = require('axios').default;

const Inicial = () => {

    function loginSucesso() {
        toast.success('Você será redirecionado!', {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: 'conectado',
            closeButton: false,
            newestOnTop: true,
        });
    }
    function loginIncorreto() {
        toast.warn('Usuário ou Senha incorreto!', {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: false,
            toastId: 'verifique',
        });
    }
    function loginErro() {
        toast.error('Sem conexão com nosso servidor. Tente novamente mais tarde.', {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: false,
            newestOnTop: true,
            theme: "colored",
        });
    }
    
    const [login, setLogin] = React.useState();
    const [senha, setSenha] = React.useState();

    const Login = (e) => {
        e.preventDefault()
        axios.get('http://localhost:3005/Logar', {
            params: {
                login: login,
                senha: senha
            }
        })
        .then((response) => {
            if(response.status === 200) {
                if(!response.data) {
                    loginIncorreto()
                    console.log("login incorreto");
                } else {
                    loginSucesso()
                    console.log(response.status);
                    console.log(response.data);
                }
            }
        })
        .catch((err) => {
            console.log(err);
        })
        .finally(() => {
            console.log("Fim do processo!");
        })

    }

return(
    <div>
        <ToastContainer />
        <div className='fundo'>
            <h1>Login CRDDRS</h1>
            <form onSubmit={Login}>
                <div className='login-user'>
                    <TextField 
                        id='login' 
                        label='Usuário' 
                        onChange={(e) => setLogin(e.target.value)}
                        color='success'
                        fullWidth required/>
                </div>
                <div className='login-user'>
                    <TextField
                        id='senha'
                        label='Senha'
                        type='password'
                        onChange={(e) => setSenha(e.target.value)}
                        color='success'
                        fullWidth required/>
                </div>
                <div className='login-user botao'>
                    <Button
                        variant='contained'
                        type='onSubmit'
                        color='success'>
                            Logar
                    </Button>
                </div>
            </form>
        </div>
    </div>
)

}
export default Inicial
