import React from "react";
import { Button, Checkbox, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, FormControl, FormControlLabel, Input, InputAdornment, Paper, Radio, RadioGroup, Slide, TextField, Typography } from "@mui/material"
import style from './SolicitaServico.module.scss';
import { Box } from "@mui/system";
import axios from "axios";

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });

export const SolicitaServico = ({ user, veiculo, setCadastroServico }) => {

    const [ dialog, setDialog ] = React.useState()

    const [ open, setOpen ] = React.useState(false);

    const handleClose = () => {
        setOpen(false);
      };


    return (
        <>
            <Dialog
                open={open}
                TransitionComponent={Transition}
                keepMounted
                onClose={handleClose}
                aria-describedby="alert-dialog-slide-description"
            >
                <DialogTitle>{dialog && dialog.titulo}</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-slide-description">
                        {dialog && dialog.conteudo}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Fechar</Button>
                </DialogActions>
            </Dialog>
            <Typography variant='h6'>Selecione o serviço desejado</Typography>
            <div className={style.opcoes}>
                <Box sx={{ width: '100%' }}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h5" align="center">GRT - GUIA DE RESPONSABILIDADE TÉCNICA</Typography>
                        <div className={style.boxlinha}>
                            <h2>Despachante / Auxiliar(DAV):</h2>
                            <TextField size="small" variant="standard" />
                        </div>
                    </Paper>
                </Box>
            </div>
        </>
    )
}
