import React from "react";
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, TableCell, TableRow, TextField, Tooltip, Typography } from "@mui/material"
import axios from "axios";
import CancelIcon from '@mui/icons-material/Cancel';
import AddchartIcon from '@mui/icons-material/Addchart';
import { Box } from "@mui/system";

export const Linha = ({ row, labelId, user }) => {

    const [open, setOpen] = React.useState({
        open: false,
        tipo: "",
        titulo: "",
        frase: "",
        texto1: "",
        texto2: ""
    });
    const [open1, setOpen1] = React.useState({
        open: false,
        tipo: '',
        frase: ''
    });
    // const [response, setResponse] = React.useState();
    const [grt, setGrt] = React.useState({
        grt1: '',
        grt2: '',
        grtTotal: ['']
    })

    var geraGRT = [];

    const [cancelar, setCancelar] = React.useState("");

    const handleClickOpen0 = () => {
        // console.log(row)
        setOpen(a => ({
            ...a,
            open: true,
            tipo: "1",
            titulo: "Adicionar GRT",
            frase: `Para adicionar as GRTs ao despachante\n` +
                ` selecionado, digite o primeiro e ultimo número dos GRTs.\n` +
                ` e clique em Adicionar.`,
            texto1: "A geração das GRTs é realizada de acordo com a grt inicial, final e a quantidade de GRTs solicitasas, caso não seja preenchida corretamente não será possível adicionar as GRTs.",
            texto2: "Ex: GRT inicial A000000, GRT final A000049 totalizando 50 GRTs.",
        }));
    };
    const handleClickOpen1 = () => {
        console.log(row)
        setOpen(a => ({
            ...a,
            open: true,
            tipo: "2",
            titulo: "Excluir solicitação de GRT",
            frase: 'Para realizar o cancelamento da solicitação é necessário escrever o motivo do cancelamento.',
            texto1: "",
            texto2: "",
        }));
    };

    const handleClose = () => {
        setOpen(a => ({
            ...a,
            open: false,
            tipo: '',
            titulo: '',
            frase: '',
            texto1: '',
            texto2: ''
        }));
    };

    const handleClose1 = () => {
        setOpen1(false);
    };

    const cancelarGrt = () => {
        console.log("Cancelado")
    };

    function addZero(num, len) {
        var nwz = String(num);
        var counter = nwz.length;
        while (counter < len) {
            nwz = "0" + nwz;
            counter++;
        }
        return nwz
    }

    const gerarGRT = () => {
        axios.post('http://localhost:3001/GerarGRT', {
            row,
            grt
        })
            .then(response => {
                // console.log(response)
                // setResponse(response.data);
                setOpen(a => ({
                    ...a,
                    open: false,
                    tipo: "",
                    titulo: "",
                    frase: "",
                    texto1: "",
                    texto2: ""
                }))
                setOpen1(a => ({
                    ...a,
                    open: true,
                    tipo: "GRTs inseridas com sucesso",
                    frase: `Foram liberadas um total de ${row.quantidade} ao Despachante ${row.nome}`
                }))
                row.situacao = "EM PROCESSO"
            })
            .catch(error => {
                setOpen(false)
                setOpen1(a => ({
                    ...a,
                    open: true,
                    tipo: "Não foi possível gerar as GRTs, tentar novamente, mais tarde!",
                    frase: `Algo ocorreu não sendo possível liberar ${row.quantidade}
                    GRTs ao Despachante ${row.nome}, por gentileza gente novamente,
                    mais tarde ou entre em contato com o suporte`
                }))
            })
    }

    const handleConfirm = () => {
        console.log("clicado")
        row["11"] = cancelar
        axios.post('http://localhost:3001/GRT/ExcluirSolicitacao', {
            row,
            grt,

        })
            .then(response => {
                // console.log(response)
                // setResponse(response.data)
                setOpen(false)
                setOpen1(true)
                row.situacao = "EM PROCESSO"
            })
            .catch(error => {
                // console.log(error)
            })
    }
    function Letrar(letra) {
        switch (letra) {
            case "A": return "B";
            case "B": return "C";
            case "C": return "D";
            case "D": return "E";
            case "E": return "F";
            case "F": return "G";
            case "G": return "H";
            case "H": return "I";
            case "I": return "J";
            case "J": return "K";
            case "K": return "L";
            case "L": return "M";
            case "M": return "N";
            case "N": return "O";
            case "O": return "P";
            case "P": return "Q";
            case "Q": return "R";
            case "R": return "S";
            case "S": return "T";
            case "T": return "U";
            case "U": return "V";
            case "V": return "X";
            case "X": return "Z";
            case "Z": return "A";
            default:
        }
    }

    React.useEffect(() => {
        if (grt.grt1.length === 7) {
            setEr1(a => ({
                ...a,
                erro: false,
                texto: ''
            }))
            setEr2(a => ({
                ...a,
                erro: false,
                texto: ''
            }))
            for (let gama = 0; gama < row.quantidade; gama++) {
                if (((grt.grt1.slice(1) * 1) + gama).toString().length < 7) {
                    geraGRT[gama] = `${grt.grt1.slice(0, 1)}${addZero(((grt.grt1.slice(1) * 1) + gama).toString(), 6)}`
                } else {
                    geraGRT[gama] = `${Letrar(grt.grt1.slice(0, 1))}${((grt.grt1.slice(1) * 1) + gama).toString().slice(1)}`
                }
                if (gama === row.quantidade - 1) {
                    setGrt(a => ({
                        ...a,
                        grt2: geraGRT[gama]
                    }))
                }
                setGrt(a => ({
                    ...a,
                    grtTotal: geraGRT
                }))
            }
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [grt.grt1])

    const [er1, setEr1] = React.useState({
        erro: false,
        texto: ''
    })
    const [er2, setEr2] = React.useState({
        erro: false,
        texto: ''
    })

    const VerValor = () => {
        if (grt.grt1.length !== 7) {
            setEr1(a => ({
                ...a,
                erro: true,
                texto: 'GRT precisa ter uma letra maiúscula e seis números'
            }))
        } else {
            setEr1(a => ({
                ...a,
                erro: false,
                texto: ''
            }))
        }
        if (grt.grt2.length !== 7) {
            setEr2(a => ({
                ...a,
                erro: true,
                texto: 'GRT precisa ter uma letra maiúscula e seis números'
            }))
        } else {
            setEr2(a => ({
                ...a,
                erro: false,
                texto: ''
            }))
        }
    }

    var validabotao = true;

    if (grt.grt1.length === 7 && grt.grt2.length === 7 && !er1.erro && !er2.erro && grt.grt2 === grt.grtTotal[row.quantidade - 1]) {
        validabotao = false;
    } else {
        validabotao = true;
    }

    return (
        <>
            <Dialog
                open={open.open}
                onClose={handleClose}
                aria-labelledby="Validar"
                aria-describedby="Validar-description"
            >
                <DialogTitle id="Validar">
                    {open.titulo}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Validar-description">
                        {open.frase}
                    </DialogContentText>
                </DialogContent>
                <Box sx={{ p: 3 }}>
                    <Typography>
                        Solicitante da GRT: {row.nome}
                    </Typography>
                    <Typography>
                        Quantidade solicitada: {row.quantidade}
                    </Typography>
                    {open.tipo === "1" &&
                        <>
                            <TextField
                                autoFocus
                                required
                                margin="dense"
                                label="GRT inicial"
                                fullWidth
                                variant="standard"
                                error={er1.erro}
                                helperText={er1.texto}
                                value={grt.grt1}
                                onBlur={VerValor}
                                onChange={(e) => setGrt(a => ({ ...a, grt1: e.target.value.toUpperCase().slice(0, 7) }))}
                            />
                            <TextField
                                required
                                margin="dense"
                                label="GRT Final"
                                fullWidth
                                variant="standard"
                                error={er2.erro}
                                helperText={er2.texto}
                                value={grt.grt2}
                                onBlur={VerValor}
                                onChange={(e) => setGrt(a => ({ ...a, grt2: e.target.value.toUpperCase().slice(0, 7) }))}
                            />
                        </>
                    }
                    {open.tipo === "2" &&
                        <>
                            <TextField
                                autoFocus
                                margin="dense"
                                label="Motivo do cancelamento."
                                fullWidth
                                multiline
                                rows={4}
                                variant="standard"
                                onChange={(e) => setCancelar(e.target.value)}
                            />
                        </>
                    }
                    <DialogContentText sx={{ mt: 4 }}>
                        {open.texto1}
                    </DialogContentText>
                    <DialogContentText sx={{ mt: 2 }}>
                        {open.texto2}
                    </DialogContentText>
                </Box>
                <DialogActions>
                    {open.tipo === "1" ? <Button disabled={validabotao} color='primary' onClick={() => gerarGRT()} >Adicionar</Button> : null}
                    {open.tipo === "2" ? <Button color='success' onClick={() => handleConfirm()} >Confirmar</Button> : null}
                    <Button color='error' onClick={handleClose} >Cancelar</Button>
                </DialogActions>
            </Dialog>
            <Dialog
                open={open1.open}
                onClose={handleClose1}
                aria-labelledby="Confirmar"
                aria-describedby="Confirmar-description"
            >
                <DialogTitle id="Confirmar">
                    {open1.tipo}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Confirmar-description">
                        {open1.frase}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button color='success' onClick={handleClose1} >Concluir</Button>
                </DialogActions>
            </Dialog>
            <TableRow
                key={row._id}
                hover
            >
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
                <TableCell
                    component="th"
                    id={labelId}
                    scope="row"
                    padding="none"
                >
                    {row.nome}
                </TableCell>
                <TableCell align="right">{row.data}</TableCell>
                <TableCell align="right">{row.hora}</TableCell>
                <TableCell align="right">{row.quantidade}</TableCell>
                <TableCell align="right">{row.situacao}</TableCell>
                <TableCell>
                    {row.situacao === "SOLICITADO" && user.usuariotipo === 'master' ?
                        <>
                            <Tooltip title='Enviar GRT'>
                                <IconButton sx={{ p: '0 5px' }} onClick={handleClickOpen0} color='primary' >
                                    <AddchartIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title='Cancelar GRT'>
                                <IconButton sx={{ p: '0 5px' }} onClick={handleClickOpen1} color='error' >
                                    <CancelIcon />
                                </IconButton>
                            </Tooltip>
                        </>
                        : null
                    }
                    {row.situacao === "SOLICITADO" && user.usuariotipo === "despachante" ?
                        <Tooltip title="Solicitar o cancelamento da solicitação">
                            <IconButton
                                onClick={cancelarGrt}
                                color='error handleClickOpen'
                            >
                                <CancelIcon />
                            </IconButton>
                        </Tooltip>
                        : null
                    }
                </TableCell>
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
            </TableRow>
        </>
    )
}
