import React from "react";
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton, TableCell, TableRow, TextField, Tooltip, Typography } from "@mui/material"
import axios from "axios";
import CancelIcon from '@mui/icons-material/Cancel';
import AddchartIcon from '@mui/icons-material/Addchart';
import { Box } from "@mui/system";

export const Linha = ({ row, labelId, user }) => {

    const [open, setOpen] = React.useState({
        open: false,
        tipo: "",
        titulo: "",
        frase: "",
    });
    const [open1, setOpen1] = React.useState(false);
    const [response, setResponse] = React.useState();
    const [grt, setGrt] = React.useState({
        grt1: '',
        grt2: ''
    })

    const handleClickOpen = () => {
        console.log(row)
        setOpen(a => ({
            ...a,
            open: true,
            tipo: "1",
            titulo: "Adicionar GRT",
            frase: `Para adicionar as GRTs ao despachante\n` +
                ` selecionado, digite o primeiro e ultimo número dos GRTs.\n` +
                ` e clique em Adicionar.`
        }));
    };

    const handleClose = () => {
        setOpen(false);
    };
    const handleClose1 = () => {
        setOpen1(false);
    };

    const cancelarGrt = () => {
        console.log("Cancelado")
    }

    const gerarGRT = () => {
        console.log("clicado")
        let z = grt.grt1.slice(1);
        let x = grt.grt2.slice(1);
        let valida = row.quantidade - z === row.quantidade - x;
        console.log(z)
        console.log(x)
        console.log(row.quantidade)
        console.log(row.quantidade - z)
        console.log(x - row.quantidade)
        console.log(valida)
    }

    const handleConfirm = () => {
        console.log("clicado")
        axios.post('http://192.168.15.49:3005/GerarGRT', {
            row,
        })
            .then(response => {
                console.log(response)
                setResponse(response.data)
                setOpen(false)
                setOpen1(true)
                row.situacao = "EM PROCESSO"
            })
            .catch(error => {
                console.log(error)
            })
    }

    return (
        <>
            <Dialog
                open={open.open}
                onClose={handleClose}
                aria-labelledby="Validar"
                aria-describedby="Validar-description"
            >
                <DialogTitle id="Validar">
                    {open.titulo}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Validar-description">
                        {open.frase}
                    </DialogContentText>
                </DialogContent>
                <Box sx={{ p: 3 }}>
                    <Typography>
                        Solicitante da GRT: {row.nome}
                    </Typography>
                    <Typography>
                        Quantidade solicitada: {row.quantidade}
                    </Typography>
                    <TextField
                        autoFocus
                        margin="dense"
                        label="GRT inicial"
                        fullWidth
                        variant="standard"
                        onChange={(e) => setGrt(a => ({...a, grt1: e.target.value}))}
                    />
                    <TextField
                        margin="dense"
                        label="GRT Final"
                        fullWidth
                        variant="standard"
                        onChange={(e) => setGrt(a => ({...a, grt2: e.target.value}))}
                    />
                    <DialogContentText sx={{mt: 4}}>
                        A geração das GRTs é realizada de acordo com a grt inicial,
                        final e a quantidade de GRTs solicitasas, caso não seja preenchida corretamente não será possível adicionar as GRTs.
                    </DialogContentText>
                    <DialogContentText sx={{mt: 2}}>
                        Ex: GRT inicial A000001, GRT final A000050 totalizando 50 GRTs.
                    </DialogContentText>
                </Box>
                <DialogActions>
                    <Button color='primary' onClick={() => gerarGRT()} >Adicionar</Button>
                    <Button color='success' onClick={() => handleConfirm()} >Confirmar</Button>
                    <Button color='error' onClick={handleClose} >Cancelar</Button>
                </DialogActions>
            </Dialog>
            <Dialog
                open={open1}
                onClose={handleClose1}
                aria-labelledby="Confirmar"
                aria-describedby="Confirmar-description"
            >
                <DialogTitle id="Confirmar">
                    {row.quantidade > 1 ? "GRT inserida com sucesso" : "GRTs inseridas com sucesso"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="Confirmar-description">
                        {row.quantidade > 1 ? `Foi liberado ${row.quantidade} GRTs ao despachante ${row.nome}` : `Será liberado 1 GRT ao despachante ${row.nome} sendo ela a ${response && response.GRTletra}${response && response.GRTnumero}`}
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button color='success' onClick={handleClose1} >Concluir</Button>
                </DialogActions>
            </Dialog>
            <TableRow
                key={row._id}
                hover
            >
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
                <TableCell
                    component="th"
                    id={labelId}
                    scope="row"
                    padding="none"
                >
                    {row.nome}
                </TableCell>
                <TableCell align="right">{row.data}</TableCell>
                <TableCell align="right">{row.hora}</TableCell>
                <TableCell align="right">{row.quantidade}</TableCell>
                <TableCell align="right">{row.situacao}</TableCell>
                <TableCell>
                    {row.situacao === "SOLICITADO" && user.usuariotipo === 'master' ?
                        <>
                            <Tooltip title='Enviar GRT'>
                                <IconButton onClick={handleClickOpen} color='primary' >
                                    <AddchartIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title='Cancelar GRT'>
                                <IconButton onClick={handleClickOpen} color='error' >
                                    <CancelIcon />
                                </IconButton>
                            </Tooltip>
                        </>
                        : null
                    }
                    {row.situacao === "SOLICITADO" && user.usuariotipo === "despachante" ?
                        <Tooltip title="Solicitar o cancelamento da solicitação">
                            <IconButton
                                onClick={cancelarGrt}
                                color='error handleClickOpen'
                            >
                                <CancelIcon />
                            </IconButton>
                        </Tooltip>
                        : null
                    }
                </TableCell>
                <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
                </TableCell>
            </TableRow>
        </>
    )
}
