import React from "react";
import { TableCell, TableRow } from "@mui/material"



export const Linha = ({ row, labelId }) => {

    const data = Date.parse(row.dataSolicitacao)

    const dia = Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(data)   

    const hora = Intl.DateTimeFormat('pt-BR', { hour: 'numeric', minute: 'numeric', second: 'numeric' }).format(data)   

    console.log(dia, hora)

    return (
        <TableRow
            key={row._id}
            hover
        >
            <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
            </TableCell>
            <TableCell
                component="th"
                id={labelId}
                scope="row"
                padding="none"
            >
                {row.nome}
            </TableCell>
            <TableCell align="right">{dia}</TableCell>
            <TableCell align="right">{hora}</TableCell>
            <TableCell align="right">{row.quantidade}</TableCell>
            <TableCell align="right">{row.situacao}</TableCell>
            <TableCell sx={{ '& > *': { borderBottom: 'unset' } }}>
            </TableCell>
        </TableRow>
    )
}
