import { TextField } from "@mui/material"
import React from "react"
import style from './Identificador.module.scss'

export const Identificador = ({ placa, setPlaca }) => {

    var passo1 = false
    
    if (placa.length > 7) {
        passo1 = true
    }

    const Placa = (e) => {
        setPlaca(e.target.value.toUpperCase().replace(/[A-Z]{3}/).slice(0,7))
    }
    const r = /[A-Z]/

    React.useEffect(() => {
        if(placa !== "") {
            if(r.test(placa[0]) === true) {} else {
                console.log('ops')
                setPlaca(placa.replace(/0-9/, ""))
            }
        }
        // if(placa[0])
        // setPlaca(a => ({
        //     ...a,
        //     placa:
        // })
        // console.log(placa[1])
        // console.log(placa[2])
        // console.log(placa[3])
        // console.log(placa[4])
        // console.log(placa[5])
        // console.log(placa[6])

    }, [placa])

    return (
        <>
            <div className={style.formulario}>
                <TextField
                    id="placa"
                    sx={{ minWidth: '30vw', margin: 2 }}
                    label='Placa'
                    placeholder='Digite a placa do veículo'
                    error={passo1}
                    helperText={passo1 ? "Placa inválida" : null}
                    value={placa}
                    onChange={Placa}
                    size='small'
                    required
                />
            </div>
        </>
    )
}
