import { Alert, Button, ButtonGroup, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Paper, Snackbar, Typography } from "@mui/material";
import { Box } from "@mui/system"
import React, { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import style from './CriarGrt.module.scss';

export const SolicitarGrt = ({ loginUser }) => {

    const [open, setOpen] = useState(false);
    const [op, setOp] = useState({
        op: false,
        tipo: 'info',
        frase: ''
    });

    const abrirDialogo = () => { setOpen(true); };
    const fecharDialogo = () => { setOpen(false); };
    const abrirAvisoPositivo = () => {
        setOp(a => ({
            ...a,
            op: true,
            tipo: 'success',
            frase: 'GRT Solicitado com exito!'
        }));
    };
    const abrirAvisoNegativo = () => {
        setOp(a => ({
            ...a,
            op: true,
            tipo: 'error',
            frase: 'Erro ao solicitar a GRT tente novamente em alguns instantes!'
        }));
    };
    const fecharAviso = () => { setOp(false); };

    const [quantidade, setQuantidade] = useState(0)

    const [valor, setValor] = useState(quantidade * 1.5)

    useEffect(() => {
        setValor(quantidade * 1.5)
    }, [quantidade])

    const valorPadrão = 1.50;

    const Solicitar = () => {

        const d = new Date();

        let GRT = {
            quantidade,
            valor,
            hora: Intl.DateTimeFormat('pt-BR', { hour: 'numeric', minute: 'numeric', second: 'numeric' }).format(d),
            data: Intl.DateTimeFormat('pt-BR', { dateStyle: 'full' }).format(d)
        }
        console.log(GRT)
        fecharDialogo()
        axios.post('http://localhost:3001/grt/solicitar', { loginUser, GRT })
            .then(res => {
                console.log(res)
                fecharDialogo()
                abrirAvisoPositivo()
            })
            .catch(err => {
                console.log(err)
                abrirAvisoNegativo()
            })
    }

    return (
        <>
            <Snackbar
                open={op.op}
                autoHideDuration={6000}
                onClose={fecharAviso}
            >
                <Alert onClose={fecharAviso} severity={op.tipo} sx={{ width: '100%' }} >
                    {op.frase}
                </Alert>
            </Snackbar>
            <Dialog
                open={open}
                onClose={fecharDialogo}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    Confirmar a solicitação das GRTs
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Ao confirmar a solicitação será cobrado o valor de R$: {valor.toFixed(2)} pela
                        quantidade de {quantidade} GRTs solicitadas.
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => Solicitar()} color='success' autoFocus>Confirmar</Button>
                    <Button onClick={fecharDialogo} color='error'>
                        Cancelar
                    </Button>
                </DialogActions>
            </Dialog>
            <Typography variant='h6' color='primary'> Solicitar GRT</Typography>
            <Box sx={{ width: '100%' }}>
                <Paper sx={{ p: 4, m: 2 }} elevation={6} >
                    <Typography variant="h5">Valor por GRT: R$ {valorPadrão.toFixed(2)} </Typography>
                    <br /><br />
                    <Typography sx={{ p: 2 }} variant='h6'>Quantidade de GRTs:  {quantidade} </Typography>
                    <Typography sx={{ p: 2 }} variant='h6'>Valor total da GRT: R$ {valor.toFixed(2)} </Typography>
                    <div className={style.listabotaocalc}>
                        <ButtonGroup variant="contained" aria-label='qtd_grt_50' >
                            <Button onClick={() => setQuantidade(quantidade + 50)}>+ 50</Button>
                            <Button disabled={quantidade <= 75} onClick={() => setQuantidade(quantidade - 50)}>-50</Button>
                        </ButtonGroup>
                    </div>
                    <br /><br />
                    <Button disabled={quantidade <= 49} sx={{ left: 1 }} onClick={() => abrirDialogo()} >Solicitar</Button>
                </Paper>
            </Box>
        </>
    )
}
